\begin{theorem}
The Axiom of Choice implies Zorn's Lemma.
\end{theorem}

\begin{theorem}
Let $a, b, c, d$ be four distinct Fibonacci numbers. Then $a, b, c, d$ do not form an arithmetic sequence, except for the case where the numbers are $0, 1, 2, 3$.
\end{theorem}

\begin{theorem}
Let $(S, \circ)$ be a semigroup. If there exists an element $s \in S$ such that for every $a \in S$, there exist elements $x, y \in S$ satisfying $s \circ x = a$ and $y \circ s = a$, then $(S, \circ)$ has an identity element.
\end{theorem}

\begin{theorem}
Any propositional formula can be expressed in conjunctive normal form (CNF).
\end{theorem}

\begin{theorem}
Let $\struct {R, \norm {\,\cdot\,} }$ be a normed division ring. If $\sequence {x_n}$ and $\sequence {y_n}$ are sequences in $R$ that converge in the norm $\norm {\,\cdot\,}$ to limits $l$ and $m$ respectively, i.e., $\lim_{n \to \infty} x_n = l$ and $\lim_{n \to \infty} y_n = m$, then the sequence $\sequence {x_n y_n}$ also converges in $R$, and its limit is given by:
\[
\lim_{n \to \infty} (x_n y_n) = lm
\]
\end{theorem}

\begin{theorem}
Let $f: \mathbb{R} \to \mathbb{R}$ be an additive function. Then for all $r \in \mathbb{Q}$ and $x \in \mathbb{R}$, we have
$$f(xr) = r f(x).$$
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a topological space, $H \subseteq S$ a subset, and $x \in S$ a point. Let $H^c$ denote the complement of $H$ in $S$. The following conditions are equivalent, defining $x$ as a limit point of $H$:
\begin{enumerate}
    \item Every open neighborhood $U$ of $x$ satisfies $H \cap (U \setminus \{x\}) \ne \emptyset$.
    \item $x \in \overline{H}$ but $x$ is not an isolated point of $H$.
    \item $x$ is an adherent point of $H$ but $x$ is not an isolated point of $H$.
    \item $H^c \cup \{x\}$ is not a neighborhood of $x$.
\end{enumerate}
\end{theorem}

\begin{theorem}
Let $M$ be a compact manifold with boundary $\partial M$. Then there is no smooth mapping $f: M \to \partial M$ such that $\partial f: \partial M \to \partial M$ is the identity map.
\end{theorem}

\begin{theorem}
The operation of addition on the set of rational numbers $\Q$ is well-defined and closed, satisfying the property that for all $x, y \in \Q$, $x+y \in \Q$.
\end{theorem}

\begin{theorem}
For any integer $n \ge 2$, and constants $a, b \in \mathbb{R}$ such that $a^2 + n^2 b^2 \neq 0$, the indefinite integral of $e^{ax} \cos^n bx$ is given by the reduction formula:
\[
\int e^{a x} \cos^n b x \,dx = \frac {e^{a x} \cos^{n - 1} b x} {a^2 + n^2 b^2} \left( a \cos b x + n b \sin b x \right) + \frac {n \left( n - 1 \right) b^2} {a^2 + n^2 b^2} \int e^{a x} \cos^{n - 2} b x \,dx + C.
\]
\end{theorem}

\begin{theorem}
Let $f: \C \to \C$ be a bounded entire function. Then $f$ is constant.
\end{theorem}

\begin{theorem*}
There is a one-to-one correspondence between Prüfer sequences and labeled trees, meaning that every labeled tree has a unique Prüfer sequence that defines it, and every Prüfer sequence defines just one labeled tree.
\end{theorem*}

\begin{theorem}
Let $(S, \tau)$ be a topological space such that $S$ is an infinite set and $\tau$ is the finite complement topology on $S$. Then the only clopen sets in $(S, \tau)$ are $S$ and $\emptyset$.
\end{theorem}

\begin{theorem}
Let $M$ be a matroid. If $A$ is a dependent subset of $M$, then there exists a circuit $C$ of $M$ such that $C \subseteq A$.
\end{theorem}

\begin{theorem}
Let $X$ be a Riemann surface. Let $\Gamma_1$, $\Gamma_2$, and $\Gamma$ be families of rectifiable curves or finite unions of rectifiable curves on $X$. If for every $\gamma \in \Gamma$, there exist $\gamma_1 \in \Gamma_1$ and $\gamma_2 \in \Gamma_2$ such that $\gamma_1 \subset \gamma$, $\gamma_2 \subset \gamma$, and $\gamma_1 \cap \gamma_2 = \varnothing$, then the extremal lengths of these families satisfy
\[
\lambda(\Gamma) \ge \lambda(\Gamma_1) + \lambda(\Gamma_2).
\]
\end{theorem}

\begin{theorem}
A connected simple graph $T$ of order $n$ is a tree if and only if it has $n-1$ edges.
\end{theorem}

Let $z_1$ and $z_2$ be distinct complex numbers in the complex plane. A straight line $L$ passing through $z_1$ and $z_2$ can be represented by the following equations:
\begin{enumerate}
    \item \textbf{General Equation:} $z(\bar{z_1} - \bar{z_2}) - \bar{z}(z_1 - z_2) + (z_1\bar{z_2} - \bar{z_1}z_2) = 0$.
    \item \textbf{Parametric Forms:}
    \begin{itemize}
        \item $z = z_1 + t(z_2 - z_1)$, for $t \in \mathbb{R}$.
        \item Alternatively, $z = (1-t)z_1 + tz_2$, for $t \in \mathbb{R}$.
    \end{itemize}
    \item \textbf{Symmetric Form:} $\frac{z - z_1}{z_2 - z_1} = \frac{\bar{z} - \bar{z_1}}{\bar{z_2} - \bar{z_1}}$.
\end{enumerate}

\begin{theorem}[Modus Tollendo Ponens in Sequent Form]
The rule of Modus Tollendo Ponens (MTP) in sequent form, which allows inferring a disjunct from a disjunction and the negation of the other disjunct, is defined by two cases:
\begin{enumerate}
    \item \textbf{Case 1:} Given $\neg P$ and $P \lor Q$, one can conclude $Q$. This is formally expressed as the sequent:
    \[ \neg P, P \lor Q \vdash Q \]
    \item \textbf{Case 2:} Given $\neg Q$ and $P \lor Q$, one can conclude $P$. This is formally expressed as the sequent:
    \[ \neg Q, P \lor Q \vdash P \]
\end{enumerate}
\end{theorem}

\begin{theorem}
The property of separability is not weakly hereditary.
\end{theorem}

\begin{theorem}
Let $(X, \Sigma, \mu)$ be a $\sigma$-finite measure space and $\theta: X \to X$ be a $\Sigma$-measurable mapping.
Suppose $\Sigma = \sigma(\mathcal G)$ for some collection $\mathcal G \subseteq \mathcal P(X)$ satisfying the following conditions:
\begin{enumerate}
    \item $\mathcal G$ is a $\pi$-system, i.e., for all $G, H \in \mathcal G$, $G \cap H \in \mathcal G$.
    \item There exists an increasing sequence of sets $(G_n)_{n \in \N}$ in $\mathcal G$ such that $G_n \uparrow X$ and $\mu(G_n) < \infty$ for all $n \in \N$.
\end{enumerate}
Furthermore, assume that for all $G \in \mathcal G$, $\mu(\theta^{-1}(G)) = \mu(G)$.
Then $\mu$ is a $\theta$-invariant measure.
\end{theorem}

\begin{theorem}
Every Noetherian integral domain is a factorization domain.
\end{theorem}

\begin{theorem}
Let $H$ be a Hilbert space and $K$ be a closed linear subspace of $H$. Let $P_K$ denote the orthogonal projection onto $K$. Then $P_K$ has the following properties:
\begin{enumerate}
    \item $P_K$ is a linear operator on $H$.
    \item For all $h \in H$, $\norm{P_K(h)} \le \norm{h}$.
    \item $P_K$ is idempotent, i.e., $P_K^2 = P_K$.
    \item The kernel of $P_K$ is $K^{\perp}$ and the range of $P_K$ is $K$.
\end{enumerate}
\end{theorem}

\begin{theorem}
For any integer $n > 1$, the $n$-th triangular number $T_n$ is not a perfect cube.
\end{theorem}

\begin{theorem}
The number $\pi^2$ is irrational.
\end{theorem}

\begin{theorem}[Euclid, Elements VIII.20]
If $a$ and $b$ are integers such that their geometric mean is an integer, then $a$ and $b$ are similar plane numbers.
\end{theorem}

\begin{theorem}
There exists a Lindelöf space that is not second-countable.
\end{theorem}

Let $M$ be a class, and let $g: M \to M$ be a mapping such that $M$ is a minimally inductive class under $g$. If $\mathcal{R}$ is a relation on $M$ satisfying the conditions:
\begin{enumerate}
    \item For all $x \in M$, $\mathcal{R}(x, \emptyset)$.
    \item For all $x, y \in M$, if $\mathcal{R}(x, y)$ and $\mathcal{R}(y, x)$, then $\mathcal{R}(x, g(y))$.
\end{enumerate}
Then $\mathcal{R}(x, y)$ for all $x, y \in M$.

