\begin{theorem}
Let $x, y: \mathbb{R} \to \mathbb{R}$ be real-valued functions. Consider the functional
$$ J[x, y] = \int_{t_0}^{t_1} \Phi(t, x, y, \dot{x}, \dot{y}) \, dt $$
where $\dot{x} = \frac{dx}{dt}$ and $\dot{y} = \frac{dy}{dt}$ denote the derivatives of $x$ and $y$ with respect to $t$.
This functional $J[x, y]$ depends exclusively on the curve in the $xy$-plane defined by the parametric equations $x = x(t), y = y(t)$, and is independent of the chosen parametric representation of the curve, if and only if the integrand $\Phi$ does not explicitly depend on $t$ and is a positively homogeneous function of degree $1$ in $\dot{x}$ and $\dot{y}$.
\end{theorem}

Let $(R, +, \cdot)$ be a ring with unity, having zero $0_R$ and unity $1_R$. Let $p$ be the characteristic of $R$. For any $a \in R$, define the function $g_a: \Z \to R$ from the integers to $R$ by $g_a(n) = n \cdot a$ for all $n \in \Z$. Then the following statements hold:
\begin{enumerate}
    \item The function $g_a$ is a group homomorphism from the additive group $(\Z, +)$ to the additive group $(R, +)$.
    \item The principal ideal $\langle p \rangle$ of $\Z$ generated by $p$ is a subset of the kernel of $g_a$, denoted $\ker(g_a)$.
    \item If $p$ divides $n$ (denoted $p \mid n$), then $n \cdot a = 0_R$.
\end{enumerate}

\begin{theorem}
The $p$-adic valuation on the integers, denoted by $\nu_p^\Z: \Z \to \Z \cup \{+\infty\}$, is a valuation.
\end{theorem}

\begin{theorem}
Every metric space $(A, d)$ is a perfectly normal space.
\end{theorem}

\begin{theorem}
Every countable discrete topological space is $\sigma$-compact.
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a topological space and let $(S_i, \tau_i)$ denote its connected components.
The space $(S, \tau)$ is regular if and only if each of its connected components $(S_i, \tau_i)$ is regular. Similarly, $(S, \tau)$ is Tychonoff (or completely regular) if and only if each of its connected components $(S_i, \tau_i)$ is Tychonoff (or completely regular).
Furthermore, if $(S, \tau)$ is normal, then each of its connected components $(S_i, \tau_i)$ is normal; however, the converse statement does not necessarily hold. The same applies to the property of being completely normal: if $(S, \tau)$ is completely normal, then each of its connected components $(S_i, \tau_i)$ is completely normal, but the converse is not generally true.
\end{theorem}

\begin{theorem}
Let $\alpha$ and $\beta$ be Dedekind cuts. If $\alpha \beta$ denotes their product, then $\alpha \beta$ is also a Dedekind cut. Consequently, the operation of multiplication is closed on the set of Dedekind cuts.
\end{theorem}

\begin{theorem}
If $S$ is a class of ordinals, then the structure $(S, \in)$ is a strict well-ordering.
\end{theorem}

\begin{theorem}
Let $G$ be a finite group such that its order, $|G|$, is greater than $2$. Then the order of its automorphism group, $|\Aut G|$, is greater than $1$.
\end{theorem}

\begin{proposition}
Let $T$ be a topological space and let $H \subseteq T$. Then the closure $\overline{H}$ of $H$ is closed in $T$.
\end{proposition}

\begin{theorem}
Let $m \in \mathbb{Z}$ be a non-zero integer. If $S = \{r_1, r_2, \dots, r_s\}$ is a complete residue system modulo $m$, then $s = |m|$.
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a regular Hausdorff space. Then $(S, \tau)$ is hereditarily normal if and only if it is paracompact.
\end{theorem}

Let $f(x)$ be the real-valued function defined on $(0, 2\pi)$ as:
\begin{equation*}
    f(x) = \begin{cases}
        (x - \pi)^2 & \text{for } 0 < x \le \pi \\
        \pi^2 & \text{for } \pi < x < 2\pi
    \end{cases}
\end{equation*}
Then its Fourier series can be expressed as:
\begin{align*}
    f(x) &\sim \frac{2\pi^2}{3} + \sum_{n=1}^\infty \left( \frac{2 \cos nx}{n^2} + \left( \frac{(-1)^n \pi}{n} - \frac{2(1 - (-1)^n)}{\pi n^3} \right) \sin nx \right) \\
    &= \frac{2\pi^2}{3} + 2 \left( \cos x + \frac{\cos 2x}{2^2} + \frac{\cos 3x}{3^2} + \cdots \right) \\
    &\quad - \left( \pi \sin x - \frac{\pi}{2} \sin 2x + \frac{\pi}{3} \sin 3x - \frac{\pi}{4} \sin 4x + \cdots \right) \\
    &\quad - \left( \frac{4}{\pi} \sin x + \frac{4}{\pi 3^3} \sin 3x + \frac{4}{\pi 5^3} \sin 5x + \cdots \right)
\end{align*}

\begin{theorem}
Let $X$ be an uncountable set. Consider the co-countable $\sigma$-algebra $\Sigma$ on $X$, defined as the collection of all subsets of $X$ that are either countable or have a countable complement. The co-countable measure $\mu: \Sigma \to [0, \infty]$ is defined by $\mu(A) = 0$ if $A$ is countable and $\mu(A) = 1$ if $A$ is uncountable. Then $\mu$ is a measure on $(X, \Sigma)$.
\end{theorem}

Let $(S, \preccurlyeq)$ be a totally ordered set. If $a, b \in S$ are elements such that $a \prec b$ and the set of elements strictly between $a$ and $b$ is empty (i.e., $\{x \in S : a \prec x \prec b\} = \varnothing$), then the strict upper closure of $a$ is equal to the weak upper closure of $b$, and the strict lower closure of $b$ is equal to the weak lower closure of $a$. That is:
\begin{align*}
a^\succ &= b^\succcurlyeq \\
b^\prec &= a^\preccurlyeq
\end{align*}
where $a^\succ = \{x \in S : a \prec x\}$, $b^\succcurlyeq = \{x \in S : b \preccurlyeq x\}$, $b^\prec = \{x \in S : x \prec b\}$, and $a^\preccurlyeq = \{x \in S : x \preccurlyeq a\}$.

\begin{theorem}
For the set of natural numbers $\N$, equipped with multiplication $\times$ and the strict ordering $<$, it holds that for all $a, b, c \in \N$, the implications $a \times c < b \times c \implies a < b$ and $a \times b < a \times c \implies b < c$ hold. These implications signify that multiplication is cancellable with respect to the strict ordering on $\N$.
\end{theorem}

\begin{theorem}
There exists a subset of the real numbers that is not Lebesgue measurable.
\end{theorem}

