\begin{theorem}
Let $(S, \tau)$ be a locally connected topological space. A subset $A \subseteq S$ is a component of $(S, \tau)$ if and only if it is a quasicomponent of $(S, \tau)$.
\end{theorem}

The Taylor series expansion for the arccosine function is given by
\begin{align*}
\arccos x &= \frac{\pi}{2} - \sum_{n=0}^\infty \frac{(2n)!}{2^{2n} (n!)^2} \frac{x^{2n+1}}{2n+1} \\
&= \frac{\pi}{2} - \left(x + \frac{1}{2} \frac{x^3}{3} + \frac{1 \cdot 3}{2 \cdot 4} \frac{x^5}{5} + \frac{1 \cdot 3 \cdot 5}{2 \cdot 4 \cdot 6} \frac{x^7}{7} + \cdots\right)
\end{align*}
This series converges for $-1 \le x \le 1$.

\begin{theorem}
Let $(x_n)$ be a sequence of real numbers such that the series $\sum_{n=1}^\infty x_n$ converges. If $(b_n)$ is a non-decreasing sequence of positive real numbers such that $b_n \to \infty$, then
\[
\lim_{n \to \infty} \frac{1}{b_n} \sum_{k=1}^n b_k x_k = 0.
\]
\end{theorem}

\begin{theorem}
For any regular expression $R$, there exists a finite state machine $F$ such that the language accepted by $F$, denoted $L(F)$, is exactly the language defined by $R$, denoted $L(R)$.
\end{theorem}

\begin{theorem}
Let $R$ be a commutative ring. Let $G$ be a right module over $R$ with scalar multiplication $\circ$. Define a new scalar multiplication $\circ' : R \times G \to G$ by $\lambda \circ' x = x \circ \lambda$ for all $\lambda \in R$ and $x \in G$. Then $G$ is an $R$-bimodule with respect to $\circ'$ as left scalar multiplication and $\circ$ as right scalar multiplication.
\end{theorem}

\begin{theorem}
Let $\mathcal{R}$ be a relation on a set $S$. If $\mathcal{R}$ is reflexive, antireflexive, non-reflexive, symmetric, asymmetric, antisymmetric, non-symmetric, transitive, antitransitive, or non-transitive, then its inverse relation $\mathcal{R}^{-1}$ also possesses that same property.
\end{theorem}

\begin{theorem}
The set of integers under addition, denoted by $(\mathbb{Z}, +)$, forms a countably infinite abelian group.
\end{theorem}

The number $50$ is the smallest positive integer that can be expressed as the sum of two square numbers in two distinct ways, specifically
\begin{align*}
50 &= 5^2 + 5^2 \\
50 &= 7^2 + 1^2.
\end{align*}

The left cosets of a subgroup $H$ in a group $G$ form a partition of $G$. Consequently, for any elements $x, y \in G$, the following equivalences hold:
\begin{align*}
x \equiv^l y \pmod H &\iff x H = y H \\
x \not\equiv^l y \pmod H &\iff x H \cap y H = \emptyset
\end{align*}

\begin{theorem}
The set of rational numbers $\mathbb{Q}$ is not a connected topological space.
\end{theorem}

\begin{theorem}
  Every topological space $(S, \tau)$ whose topology $\tau$ is generated by a partition of its underlying set $S$ is pseudometrizable.
\end{theorem}

\begin{theorem}
For a non-zero constant $a$, the indefinite integral of $\frac{1}{\cos^3(ax)}$ with respect to $x$ is given by:
\[
\int \frac{\mathrm{d}x}{\cos^3(ax)} = \frac{\sin(ax)}{2a\cos^2(ax)} + \frac{1}{2a} \ln\left|\tan\left(\frac{\pi}{4} + \frac{ax}{2}\right)\right| + C
\]
\end{theorem}

\begin{theorem}
Let $R$ be an infinite index set. If the series $\displaystyle \sum_{i \in R} a_i$ is absolutely convergent, then for any permutation $\pi$ of $R$, we have
\[
\sum_{j \in R} a_j = \sum_{j \in R} a_{\pi(j)}.
\]
\end{theorem}

\begin{theorem}
Let $n \in \mathbb{Z}^+$ be a positive integer with prime factorization $n = \prod_{i=1}^s p_i^{m_i}$, where $p_i$ are distinct prime numbers and $m_i \ge 1$ for all $i$. The number of non-isomorphic abelian groups of order $n$, denoted by $\nu_a(n)$, is given by
$$ \nu_a(n) = \prod_{i=1}^s \nu_a(p_i^{m_i}) $$
where $\nu_a(p_i^{m_i})$ is the number of partitions of the integer $m_i$.
\end{theorem}

\begin{theorem}
Let $R$ be an ordered ring. If $x, y \in R$ satisfy $0 < x < y$, then for any positive integer $n$, we have $0 < x^n < y^n$.
\end{theorem}

\begin{theorem}
Let $f$ be a continuous real-valued function on a closed interval $[a, b]$ where $a < b$. Then:
\begin{enumerate}
    \item If $f(x) \ge 0$ for all $x \in [a, b]$, then $\displaystyle \int_a^b f(x) \, dx \ge 0$.
    \item If $f(x) > 0$ for all $x \in [a, b]$, then $\displaystyle \int_a^b f(x) \, dx > 0$.
    \item If $f(x) \le 0$ for all $x \in [a, b]$, then $\displaystyle \int_a^b f(x) \, dx \le 0$.
    \item If $f(x) < 0$ for all $x \in [a, b]$, then $\displaystyle \int_a^b f(x) \, dx < 0$.
\end{enumerate}
\end{theorem}

\begin{theorem}
The class $\mathcal N$ of all cardinal numbers is a proper class.
\end{theorem}

For $0 < p \le 2m+1$, the following integral identity holds:
\begin{align*}
\int \frac{x^{p - 1}}{x^{2m + 1} - a^{2m + 1}} \, \mathrm{d}x &= \frac{1}{(2m + 1)a^{2m - p + 1}} \left[ -2 \sum_{k=1}^m \sin\left(\frac{2kp\pi}{2m + 1}\right) \arctan\left(\frac{x - a \cos\left(\frac{2k\pi}{2m + 1}\right)}{a \sin\left(\frac{2k\pi}{2m + 1}\right)}\right) \right. \\
&\quad \left. {} + \sum_{k=1}^m \cos\left(\frac{2kp\pi}{2m + 1}\right) \ln\left(x^2 - 2ax \cos\left(\frac{2k\pi}{2m + 1}\right) + a^2\right) \right. \\
&\quad \left. {} + \ln(x - a) \right].
\end{align*}

Let $(X, \Sigma, \mu)$ be a measure space, and let $p \in \mathbb{R}$ with $p \ge 1$. Let $(f_n)_{n \in \mathbb{N}}$ be a sequence of $p$-integrable functions from $X$ to $\mathbb{R}$, and let $f: X \to \mathbb{R}$ be a measurable function. Suppose that $f_n$ converges in measure to $f$, denoted by $\mu\text{-}\lim_{n \to \infty} f_n = f$.

Then, the following statements are equivalent:
\begin{enumerate}
    \item $\displaystyle \lim_{n \to \infty} \left\Vert{f_n - f}\right\Vert_p = 0$, where $\left\Vert{\cdot}\right\Vert_p$ is the $p$-seminorm (i.e., $f_n$ converges to $f$ in $\mathcal{L}^p$).
    \item The collection $(\left\vert{f_n}\right\vert^p)_{n \in \mathbb{N}}$ is uniformly integrable.
    \item $\displaystyle \lim_{n \to \infty} \int \left\vert{f_n}\right\vert^p \, \mathrm{d}\mu = \int \left\vert{f}\right\vert^p \, \mathrm{d}\mu$.
\end{enumerate}
This equivalence holds if $(X, \Sigma, \mu)$ is a $\sigma$-finite measure space. If $(X, \Sigma, \mu)$ is not $\sigma$-finite, then statements (1) and (3) must be replaced by, respectively:
\begin{enumerate}
    \item[($1'$)] The sequence $(f_n)_{n \in \mathbb{N}}$ converges in $\mathcal{L}^p$.
    \item[($3'$)] $\displaystyle \lim_{n \to \infty} \int \left\vert{f_n}\right\vert^p \, \mathrm{d}\mu$ exists in $\mathbb{R}$.
\end{enumerate}
In the non-$\sigma$-finite case, the equivalent statements are ($1'$), (2), and ($3'$).

\begin{theorem}
The Arens-Fort space is not a first-countable space.
\end{theorem}

\begin{theorem}
The following statements are equivalent definitions of Euler's number $e$:
\begin{enumerate}
    \item The number $e$ is the sum of the infinite series:
    \[ e = \sum_{n=0}^{\infty} \frac{1}{n!} \]
    \item The number $e$ is the limit of the sequence:
    \[ e = \lim_{n \to \infty} \left(1 + \frac{1}{n}\right)^n \]
    \item The number $e$ is the unique positive real number such that:
    \[ \int_1^e \frac{1}{t} \, dt = 1 \]
    \item The number $e$ is the unique positive real number for which the exponential function $f(x) = e^x$ satisfies $f'(x) = f(x)$ and $f(0) = 1$.
\end{enumerate}
\end{theorem}
