\begin{theorem}
The minimal infinite successor set $\omega$ is a subset of every infinite successor set.
\end{theorem}

\begin{theorem}
For any positive integer $n$, the $n$-th cyclotomic polynomial $\Phi_n(x)$ can be expressed as:
$$ \Phi_n(x) = \prod_{d \mid n} (x^d - 1)^{\mu(n/d)} $$
where the product runs over all positive divisors $d$ of $n$, and $\mu$ is the Möbius function.
\end{theorem}

\begin{theorem}
The set of integers under addition, denoted by $(\mathbb{Z}, +)$, forms an abelian group.
\end{theorem}

\begin{theorem}
For any positive real number $a \in \mathbb{R}_{>0}$ and any real numbers $x, y \in \mathbb{R}$, it holds that
\[
a^x a^y = a^{x + y}.
\]
\end{theorem}

\begin{theorem}
Let $F$ be a field, and let $F[X]$ denote the ring of polynomials in the indeterminate $X$ with coefficients in $F$. If $J$ is a non-zero ideal of $F[X]$, then there exists a unique monic polynomial $f \in F[X]$ such that $J = \langle f \rangle$, where $\langle f \rangle$ denotes the principal ideal generated by $f$ in $F[X]$.
\end{theorem}

\begin{theorem}
An excluded point space is a $T_0$ (Kolmogorov) space.
\end{theorem}

\begin{theorem}
An uncountable discrete topological space is not a Lindelöf space.
\end{theorem}

