\begin{theorem}
The set of irrational numbers, denoted by $\mathbb{R} \setminus \mathbb{Q}$, forms a $G_\delta$ set in the real number line $\mathbb{R}$ equipped with the usual (Euclidean) topology.
\end{theorem}

\end{document}

\begin{theorem}
The variational equation of Euler's equation is Jacobi's equation.
\end{theorem}

\begin{theorem}
Let $S \times T$ be the Cartesian product of two finite sets $S$ and $T$. Then its cardinality is given by
\[ \left| S \times T \right| = \left| S \right| \times \left| T \right| \]
where $\left| X \right|$ denotes the cardinality of a set $X$.
\end{theorem}

\begin{theorem}
Let $P(n)$ be a propositional function defined for integers $n$. If there exists an integer $n_0$ such that the following two conditions hold:
\begin{enumerate}
    \item $P(n_0)$ is true, and
    \item for every integer $k \ge n_0$, the implication $P(k) \implies P(k+1)$ holds,
\end{enumerate}
then $P(n)$ is true for all integers $n \ge n_0$.
\end{theorem}

\begin{theorem}
Let $(X, \tau)$ be a topological space. Then the collection of open sets $\tau$, ordered by set inclusion $\subseteq$, forms a complete lattice.
\end{theorem}

The second-order ordinary differential equation
\begin{equation}
y'' + 2 y' + y = x e^{-x}
\end{equation}
has the general solution
$$y = e^{-x} \left( C_1 + C_2 x + \frac{x^3}{6} \right).$$

\begin{theorem}
The Arens-Fort space $(S, \tau)$ is not compact.
\end{theorem}

Let $\mathbf f(x) = \left\langle{f_1 (x), f_2 (x), \ldots, f_n (x)}\right\rangle$, $\mathbf g(x) = \left\langle{g_1 (x), g_2 (x), \ldots, g_n (x)}\right\rangle$, and $\mathbf h(x) = \left\langle{h_1 (x), h_2 (x), \ldots, h_n (x)}\right\rangle$ be differentiable vector-valued functions. Then, the derivative of their scalar triple product is given by the formula:
\begin{equation*}
\frac{d}{dx} \left(\mathbf f \cdot \left(\mathbf g \times \mathbf h\right)\right) = \frac{d\mathbf f}{dx} \cdot \left(\mathbf g \times \mathbf h\right) + \mathbf f \cdot \left(\frac{d\mathbf g}{dx} \times \mathbf h\right) + \mathbf f \cdot \left(\mathbf g \times \frac{d\mathbf h}{dx}\right)
\end{equation*}

\begin{problem}
Let $A$ and $B$ be sets such that $|A| \ge 2$ and $|B| \ge 2$. Prove that there exists a subset $W \subseteq A \times B$ such that $W$ is not of the form $A' \times B'$ for any subsets $A' \subseteq A$ and $B' \subseteq B$.
\end{problem}

\begin{theorem}
A finite multigraph possesses an Eulerian trail if and only if it is connected and has at most two vertices of odd degree. Moreover, any Eulerian trail that is not an Eulerian circuit must begin and end at distinct vertices of odd degree.
\end{theorem}

\begin{theorem}
Let $f: [a,b] \to \mathbb{R}$ be a real-valued function that is continuous on the closed interval $[a,b]$ and differentiable on the open interval $(a,b)$. If $f$ is convex on $(a,b)$, then for any $\xi \in (a,b)$ and for all $x \in [a,b]$, the following inequality holds:
\[
f(x) - f(\xi) \ge f'(\xi)(x - \xi)
\]
\end{theorem}

\begin{theorem}
The second-order ordinary differential equation
\begin{equation*}
y'' + y' - 2 y = 0
\end{equation*}
has the general solution
\begin{equation*}
y = C_1 e^x + C_2 e^{-2 x}.
\end{equation*}
\end{theorem}

\begin{theorem}
Let $I$ be an indexing set, and let $\left\{B_\alpha\right\}_{\alpha \in I}$ be an indexed family of subsets of a set $S$. If $A$ is a set such that $A \subseteq B_\alpha$ for all $\alpha \in I$, then $A \subseteq \bigcap_{\alpha \in I} B_\alpha$.
\end{theorem}

Let $(X, \Sigma, \mu)$ be a measure space and $f: X \to \overline \R$ be a $\mu$-integrable function. Then the following are equivalent:
\begin{enumerate}
    \item $f = 0$ almost everywhere.
    \item $\int \lvert f \rvert \, d\mu = 0$.
\end{enumerate}

\begin{theorem}
Let $S$ and $T$ be sets, and let $\mathbb{T}$ be a family of subsets of $T$. Then the following generalized De Morgan's Laws for set difference hold:
\begin{enumerate}
    \item \textbf{Difference with Intersection:} $S \setminus \bigcap_{A \in \mathbb{T}} A = \bigcup_{A \in \mathbb{T}} (S \setminus A)$
    \item \textbf{Difference with Union:} $S \setminus \bigcup_{A \in \mathbb{T}} A = \bigcap_{A \in \mathbb{T}} (S \setminus A)$
\end{enumerate}
\end{theorem}

Prove that the determinant $D$ of the $n \times n$ matrix
\[ D = \begin{vmatrix}
  1 & b_{12} & \cdots & b_{1n} \\
  0 & b_{22} & \cdots & b_{2n} \\
\vdots & \vdots & \ddots & \vdots \\
  0 & b_{n2} & \cdots & b_{nn}
\end{vmatrix} \]
is equal to the determinant of its $(n-1) \times (n-1)$ submatrix obtained by removing the first row and first column, specifically
\[ D = \begin{vmatrix}
  b_{22} & \cdots & b_{2n} \\
\vdots & \ddots & \vdots \\
  b_{n2} & \cdots & b_{nn}
\end{vmatrix} \]

\begin{theorem}
If Schanuel's Conjecture is true, then the logarithm of $\pi$, $\ln \pi$, is transcendental.
\end{theorem}

\begin{theorem}[Euclid VII.20]
Let $a, b, c, d \in \Z_{>0}$ be positive integers. If the fraction $\dfrac{a}{b}$ is in simplest form, and $\dfrac{a}{b} = \dfrac{c}{d}$, then $a \divides c$ and $b \divides d$.
\end{theorem}

