\begin{theorem}
Let $R$ be a commutative ring and $G$ be a left $R$-module with scalar multiplication $\circ$. Define a new operation $\circ' : G \times R \to G$ by $x \circ' \lambda = \lambda \circ x$ for all $\lambda \in R$ and $x \in G$. Then $G$ is a right $R$-module with respect to the operation $\circ'$.
\end{theorem}

\begin{theorem}
Let $X$ be a set, and let $\equiv$ and $\sim$ be equivalence relations on $X$. The following statements are equivalent:
\begin{enumerate}
    \item $\equiv$ is finer than $\sim$, \text{i.e.}, for all $x, y \in X$, if $x \equiv y$ then $x \sim y$.
    \item The graph of $\equiv$ is contained in the graph of $\sim$.
    \item Every $\equiv$-equivalence class is contained in a $\sim$-equivalence class.
    \item Every $\sim$-equivalence class is saturated under $\equiv$.
\end{enumerate}
\end{theorem}

\begin{theorem}
Let $(X, d)$ be a metric space. If $\langle x_n \rangle$ is a sequence in $(X, d)$, then $\langle x_n \rangle$ can have at most one limit.
\end{theorem}

\begin{theorem}
Let $G$ be a group. If $H$ is a characteristic subgroup of $G$ and $K$ is a characteristic subgroup of $H$, then $K$ is a characteristic subgroup of $G$.
\end{theorem}

