\begin{theorem}
Let $(G, \circ, \preceq)$ be an ordered group with identity element $e$. Let $\prec$ denote the reflexive reduction of $\preceq$. For any elements $x, y \in G$, the following equivalences hold:
\begin{enumerate}
    \item $x \preceq y \iff e \preceq y \circ x^{-1}$
    \item $x \preceq y \iff e \preceq x^{-1} \circ y$
    \item $x \preceq y \iff x \circ y^{-1} \preceq e$
    \item $x \preceq y \iff y^{-1} \circ x \preceq e$
    \item $x \prec y \iff e \prec y \circ x^{-1}$
    \item $x \prec y \iff e \prec x^{-1} \circ y$
    \item $x \prec y \iff x \circ y^{-1} \prec e$
    \item $x \prec y \iff y^{-1} \circ x \prec e$
\end{enumerate}
\end{theorem}

Prove that for any real number $x$ for which $\tan x$ is defined, the following identity holds:
\begin{equation*}
    \cos x =
    \begin{cases}
        +\frac{1}{\sqrt{1+\tan^2 x}} & \text{if there exists an integer } n \text{ such that } \left(2n - \frac{1}{2}\right)\pi < x < \left(2n + \frac{1}{2}\right)\pi \\
        -\frac{1}{\sqrt{1+\tan^2 x}} & \text{if there exists an integer } n \text{ such that } \left(2n + \frac{1}{2}\right)\pi < x < \left(2n + \frac{3}{2}\right)\pi
    \end{cases}
\end{equation*}

\begin{theorem}
The operation of multiplication on the set of rational numbers $\Q$ is commutative, meaning that for all $x, y \in \Q$, it holds that $x \times y = y \times x$.
\end{theorem}

Let $S$ be a finite set. If $\mathscr B$ is a non-empty set of subsets of $S$ satisfying the base axiom, which states:
\begin{enumerate}
    \item For any $B_1, B_2 \in \mathscr B$, if $B_1 \subseteq B_2$, then $B_1 = B_2$.
    \item For any $B_1, B_2 \in \mathscr B$ and any element $x \in B_1 \setminus B_2$, there exists an element $y \in B_2 \setminus B_1$ such that $(B_1 \setminus \{x\}) \cup \{y\} \in \mathscr B$.
\end{enumerate}
Then $\mathscr B$ is the set of bases of a matroid on $S$.

\begin{theorem}
Let $T_1$ and $T_2$ be topological spaces, and let $f: T_1 \to T_2$ be a continuous mapping. If $T_1$ is compact, then its image $f(T_1)$ under $f$ is also compact. Consequently, compactness is a continuous invariant.
\end{theorem}

\begin{theorem}
Let $T = (S, \{\emptyset, S\})$ be an indiscrete topological space. Then $T$ is non-meager.
\end{theorem}

\begin{theorem}
In an ordered set $(S, \preceq)$, the strict lower closure $p^\prec$ of any element $p \in S$ is a lower set.
\end{theorem}

\begin{theorem}
Let $x, y, z$ be integers such that $z > 0$. Then the following equivalences hold:
\begin{enumerate}
    \item $x < y \iff z x < z y$
    \item $x \le y \iff z x \le z y$
\end{enumerate}
\end{theorem}

\begin{theorem}
Every perfect number $n$---that is, every $n \in \mathbb{Z}_{>0}$ such that the sum of its positive divisors $\sigma(n)$ equals $2n$---is an Ore number; that is, the harmonic mean of its positive divisors, $\frac{n \tau(n)}{\sigma(n)}$, is an integer.
\end{theorem}

\begin{theorem}
Let $S$ be a compact subspace of the $n$-dimensional Euclidean space $\R^n$. If $f: S \to \R$ is a continuous function, then $f$ is bounded.
\end{theorem}

\begin{theorem}
Any propositional formula can be expressed in negation normal form (NNF).
\end{theorem}

Let $(G, \circ)$ be a group with identity element $e$, and let $N$ be a subgroup of $G$. Then $N$ is a normal subgroup of $G$ if and only if any of the following equivalent conditions holds:
\begin{enumerate}
    \item For all $g \in G$, $g \circ N \circ g^{-1} = N$.
    \item For all $g \in G$, $g^{-1} \circ N \circ g = N$.
\end{enumerate}

\begin{theorem}
Let $G$ be an $n$-dimensional vector space over a field. Let $G^*$ denote its dual space and $G^{**}$ denote the dual space of $G^*$. Let $J: G \to G^{**}$ be the evaluation isomorphism.
Let $M$ be an $m$-dimensional subspace of $G$, and let $M^\circ$ be its annihilator in $G^*$.
Let $N$ be a $p$-dimensional subspace of $G^*$.
Then the following statements hold:
\begin{enumerate}
    \item $M^\circ$ is an $(n-m)$-dimensional subspace of $G^*$, and $M^{\circ \circ} = J(M)$.
    \item $J^{-1}(N^\circ)$ is an $(n-p)$-dimensional subspace of $G$.
    \item The map $M \mapsto M^\circ$ is a bijection from the set of all $m$-dimensional subspaces of $G$ onto the set of all $(n-m)$-dimensional subspaces of $G^*$.
    \item Its inverse is the bijection $N \mapsto J^{-1}(N^\circ)$.
\end{enumerate}
\end{theorem}

\begin{theorem}
The set of even natural numbers is infinite.
\end{theorem}

\begin{theorem}
Let $R$ and $S$ be totally ordered integral domains. Let $K$ and $L$ be their respective totally ordered fields of quotients. If $\phi: R \to S$ is an order embedding, then there exists a unique order embedding $\psi: K \to L$ such that $\psi$ extends $\phi$. Specifically, for any $x \in R$ and $y \in R$ with $y \neq 0$, $\psi\left(\frac{x}{y}\right) = \frac{\phi(x)}{\phi(y)}$. Furthermore, if $\phi$ is an order isomorphism, then $\psi$ is also an order isomorphism.
\end{theorem}

Let $f, g : \R \to \C$ be Schwarz functions, and let $\hat f$ and $\hat g$ denote their respective Fourier transforms. Then:
\begin{enumerate}
    \item $\hat f$ and $\hat g$ are also Schwarz functions.
    \item For all $x \in \R$, the double Fourier transform satisfies $\widehat{\hat f}(x) = f(-x)$.
    \item If $f * g$ denotes the convolution of $f$ and $g$, then its Fourier transform is given by $\widehat{f * g} = \hat f \hat g$.
\end{enumerate}

Let $(S, \vee, \wedge)$ be a Boolean algebra. Then for all $a, b \in S$, the following absorption laws hold:
\begin{align*} a &= a \vee (a \wedge b) \\ a &= a \wedge (a \vee b) \end{align*}
This demonstrates that $\vee$ absorbs $\wedge$, and $\wedge$ absorbs $\vee$.

\begin{theorem}
If $S$ and $T$ are disjoint sets, then for any $S' \subseteq S$ and $T' \subseteq T$, the sets $S'$ and $T'$ are also disjoint.
\end{theorem}

\begin{theorem}
If $\alpha$ and $\beta$ are the roots of the quadratic equation $a x^2 + b x + c = 0$, then their product is given by $\alpha \beta = \dfrac{c}{a}$.
\end{theorem}
