\begin{theorem}
Let $A$ and $B$ be two events in a probability space. If $A \subseteq B$, then $\Pr(A) \le \Pr(B)$.
\end{theorem}

\begin{theorem}
Let $n$ be a positive integer such that $n=2^k$ for some positive integer $k$. Then $n$ is an almost perfect number, meaning that the sum of its positive divisors, $\sigma(n)$, is equal to $2n-1$.
\end{theorem}

\begin{theorem}
For all positive natural numbers $m$ and $n$, it holds that $(m+1)+n = (m+n)+1$.
\end{theorem}

In a $3$-dimensional Euclidean space $M$, let $S$ be a sphere. Consider a curve $\gamma$ lying on $S$, parameterized in Cartesian coordinates by $(x, y(x), z(x))$, where $y(x)$ and $z(x)$ are real functions. If $\gamma$ connects two given points $\left( x_0, y_0, z_0 \right)$ and $\left( x_1, y_1, z_1 \right)$ on $S$ and represents the path of minimum length (a geodesic) between them, then $\gamma$ satisfies the following Euler-Lagrange equations, where $\lambda(x)$ is a Lagrange multiplier:
\begin{align*}
2 y \lambda(x) - \frac{d}{dx} \left( \frac{y'}{\sqrt{1 + y'^2 + z'^2}} \right) &= 0 \\
2 z \lambda(x) - \frac{d}{dx} \left( \frac{z'}{\sqrt{1 + y'^2 + z'^2}} \right) &= 0
\end{align*}

\begin{theorem}
For a constant coefficient homogeneous linear second-order ordinary differential equation
\[ y'' + p y' + q y = 0, \]
the function $y = e^{m_1 x}$ is a solution if and only if $m_1$ is a root of its auxiliary equation $m^2 + p m + q = 0$.
\end{theorem}

\begin{theorem}
For any positive integer $n$, if $T_j$ denotes the $j$th triangular number, then the sum of the first $n$ triangular numbers is given by
\begin{align*}
\sum_{j=1}^n T_j &= T_1 + T_2 + T_3 + \dotsb + T_n \\
&= \frac{n(n+1)(n+2)}{6}.
\end{align*}
\end{theorem}

\begin{theorem}
Let $G$ be a group and $N$ be a normal subgroup of $G$. Then there exists a group homomorphism whose kernel is $N$.
\end{theorem}

\begin{theorem}[Principle of Class Induction]
Let $M$ be a class, $g: M \to M$ a mapping on $M$, and $b \in M$. Suppose that $M$ is the smallest class containing $b$ and closed under $g$. Let $P$ be a predicate on $M$. If the following two conditions hold:
\begin{enumerate}
    \item $P(b)$ is true.
    \item For all $x \in M$, if $P(x)$ is true, then $P(g(x))$ is true.
\end{enumerate}
Then $P(x)$ is true for all $x \in M$.
\end{theorem}

Let $(R, \norm{\cdot})$ be a non-Archimedean normed division ring with zero $0_R$ and unity $1_R$. Let $\mathcal{O}$ be the valuation ring induced by the non-Archimedean norm $\norm{\cdot}$, defined as $\mathcal{O} = \left\{x \in R : \norm{x} \le 1\right\}$. Let $\mathcal{P}$ be the valuation ideal induced by the non-Archimedean norm $\norm{\cdot}$, defined as $\mathcal{P} = \left\{x \in R : \norm{x} < 1\right\}$. Then $\mathcal{P}$ is an ideal of $\mathcal{O}$, and the following properties hold:
\begin{enumerate}
    \item $\mathcal{P}$ is a maximal left ideal.
    \item $\mathcal{P}$ is a maximal right ideal.
    \item The quotient ring $\mathcal{O} / \mathcal{P}$ is a division ring.
\end{enumerate}

\begin{theorem}
There exists a compact topological space that is not sequentially compact.
\end{theorem}

\begin{theorem}
For every natural number $n > 1$, the number of partitions of $n$, denoted by $p(n)$, satisfies $p(n) < 2^n$.
\end{theorem}

\begin{theorem}
The biconditional operator is transitive.
\end{theorem}

\begin{theorem}
Let $\mathcal{S}$ be a system of sets. If there exists an element $U \in \mathcal{S}$ such that for every set $A \in \mathcal{S}$, $A \subseteq U$, then this element $U$ is unique.
\end{theorem}

\begin{theorem}
Let $f: T_1 \to T_2$ be a function between topological spaces $T_1$ and $T_2$. Then $f$ is continuous if either of the following conditions holds:
\begin{enumerate}
    \item There exists a finite collection of closed subsets $\{V_i\}_{i=1}^n$ of $T_1$ such that $T_1 = \bigcup_{i=1}^n V_i$, and for each $i \in \{1, \dots, n\}$, the restriction $f|_{V_i}: V_i \to T_2$ is continuous (when $V_i$ is equipped with the subspace topology).
    \item There exists an arbitrary collection of open subsets $\{U_i\}_{i \in I}$ of $T_1$ (for some indexing set $I$) such that $T_1 = \bigcup_{i \in I} U_i$, and for each $i \in I$, the restriction $f|_{U_i}: U_i \to T_2$ is continuous (when $U_i$ is equipped with the subspace topology).
\end{enumerate}
\end{theorem}

\begin{theorem}
If $a, b, c, d$ are real numbers such that $a > b$ and $c > d$, then $a + c > b + d$.
\end{theorem}

\begin{theorem}
Let $a, b \in \mathbb{Q}$ such that $a < b$. Then there exists an irrational number $\xi \in \mathbb{R} \setminus \mathbb{Q}$ such that $a < \xi < b$.
\end{theorem}

\begin{theorem}
Let $m, n, r \in \mathbb{Z}$. Let $k\mathbb{Z}$ denote the set of integer multiples of $k$, and let $\gcd(m, n)$ denote the greatest common divisor of $m$ and $n$. If $m\mathbb{Z} \subseteq r\mathbb{Z}$ and $n\mathbb{Z} \subseteq r\mathbb{Z}$, then $\gcd(m, n)\mathbb{Z} \subseteq r\mathbb{Z}$.
\end{theorem}

Let $(X, \preceq, \tau)$ be a linearly ordered space, and let $Y$ be a non-empty subset of $X$. Then $Y$ is a compact subspace of $(X, \tau)$ if and only if both of the following conditions hold:
\begin{enumerate}
    \item For every non-empty subset $S \subseteq Y$, $S$ has a supremum and an infimum in $X$.
    \item For every non-empty subset $S \subseteq Y$, $\sup S \in Y$ and $\inf S \in Y$.
\end{enumerate}

\begin{theorem}
Let $H$ be a Hilbert space, and let $L$ be a bounded linear functional on $H$. The norm of $L$ can be equivalently defined by any of the following four expressions:
\begin{align*}
\left\|{L}\right\|_1 &= \sup \left\{{\left|{Lh}\right|: \left\|{h}\right\| \le 1}\right\} \\
\left\|{L}\right\|_2 &= \sup \left\{{\left|{Lh}\right|: \left\|{h}\right\| = 1}\right\} \\
\left\|{L}\right\|_3 &= \sup \left\{{\frac {\left|{Lh}\right|} {\left\|{h}\right\|}: h \in H\setminus \left\{\mathbf 0 \right\}}\right\} \\
\left\|{L}\right\|_4 &= \inf \left\{{c > 0: \forall h \in H: \left|{Lh}\right| \le c \left\|{h}\right\|}\right\}
\end{align*}
Consequently, these definitions are all equal:
\[
\left\|{L}\right\|_1 = \left\|{L}\right\|_2 = \left\|{L}\right\|_3 = \left\|{L}\right\|_4.
\]
\end{theorem}

\begin{theorem}
For any non-negative real numbers $a_1, a_2, \ldots, a_n$ and $b_1, b_2, \ldots, b_n$, and for any real number $p$ such that $p > 1$, the following inequality holds:
\[
\left(\sum_{k=1}^n (a_k + b_k)^p\right)^{1/p} \le \left(\sum_{k=1}^n a_k^p\right)^{1/p} + \left(\sum_{k=1}^n b_k^p\right)^{1/p}
\]
\end{theorem}

\begin{theorem}
Let $\phi$ be a sentence in the first-order language of rings, $\mathcal{L}_r = \{0, 1, +, -, \cdot\}$, where $0$ and $1$ are constant symbols and $+, -, \cdot$ are binary function symbols. The following statements are equivalent:
\begin{enumerate}
    \item $\phi$ is true in every algebraically closed field of characteristic $0$.
    \item $\phi$ is true in some algebraically closed field of characteristic $0$.
    \item $\phi$ is true in algebraically closed fields of characteristic $p$ for arbitrarily large primes $p$.
    \item $\phi$ is true in algebraically closed fields of characteristic $p$ for sufficiently large primes $p$.
\end{enumerate}
Furthermore, since the field of complex numbers $\mathbb{C}$ is an algebraically closed field of characteristic $0$, these conditions are equivalent to $\phi$ being true in $\mathbb{C}$.
\end{theorem}

There exist exactly $8$ distinct convex deltahedra:
\begin{enumerate}
    \item the regular tetrahedron (4 faces);
    \item the triangular bipyramid (6 faces);
    \item the regular octahedron (8 faces);
    \item the pentagonal bipyramid (10 faces);
    \item the snub disphenoid (12 faces), formed by splitting a regular tetrahedron into two wedges and joining them with a band of $8$ equilateral triangles;
    \item the triaugmented triangular prism (14 faces), formed by attaching $3$ square pyramids to a triangular prism;
    \item the gyroelongated square bipyramid (16 faces), formed by attaching $2$ square pyramids to a square antiprism;
    \item the regular icosahedron (20 faces).
\end{enumerate}

\begin{theorem}
For $x \neq 0$ and $a \neq 0$, the following identity holds:
\[ \int \frac{\mathrm{d}x}{x \left(x^4 + a^4\right)} = \frac{1}{4a^4} \ln \left(\frac{x^4}{x^4 + a^4}\right) \]
\end{theorem}

\begin{theorem}
If a twice differentiable functional $J$ has an extremum at $y=\hat y$, and its second variation $\delta^2 J(\hat y; h)$ is strongly positive with respect to $h$, then $J$ attains a minimum at $y=\hat y$.
\end{theorem}

\begin{theorem}
Let $(S, \circ)$ be an algebraic structure. If the operation $\circ$ is left cancellable and commutative, then $\circ$ is also right cancellable.
\end{theorem}

\begin{theorem}
Let $X$ be a metric space, $D$ a dense subset of $X$, and $f: D \to \mathbb{R}$ a uniformly continuous mapping. Then there exists a unique continuous extension of $f$ to $X$.
\end{theorem}

\begin{theorem}
The external direct product of a finite sequence of abelian groups is itself an abelian group.
\end{theorem}

The following identity holds for all real numbers $x$ and $y$:
\begin{equation*}
\sinh x + \sinh y = 2 \sinh \left(\frac{x + y}{2}\right) \cosh \left(\frac{x - y}{2}\right)
\end{equation*}

\begin{theorem}
Consider the quadratic equation
\[ ax - x^2 = \frac{b^2}{4}. \]
Then $x$ and $a - x$ are incommensurable if and only if $\sqrt{a^2 - b^2}$ and $a$ are incommensurable. (Euclid, Proposition X.18)
\end{theorem}

\begin{theorem}
A pentatope is self-dual.
\end{theorem}

\begin{theorem}
There exist no configurations of $7$ or $8$ straight lines such that each line intersects exactly $3$ other lines.
\end{theorem}

\begin{theorem}
Let $a, b \in \mathbb{R}$ with $a < b$. If a function $f: [a, b] \to \mathbb{R}$ is continuous on $[a, b]$, differentiable on $(a, b)$, and its derivative $f'$ is bounded on $(a, b)$, then $f$ is absolutely continuous.
\end{theorem}

\begin{theorem}
There exists a separable topological space that is not second-countable.
\end{theorem}

