\newtheorem*{theorem}{Theorem}

\begin{document}

\begin{theorem}
A non-empty finite semigroup $(S, \circ)$ in which all elements are cancellable is a group.
\end{theorem}

\end{document}

\begin{theorem}
For any sets $S$ and $T$, and for any subset $A$ of $S$, it holds that
\[ A \cap T = \emptyset \iff A \subseteq S \setminus T. \]
\end{theorem}

\begin{theorem}
For any natural numbers $m, n,$ and $k$, it holds that $m < n$ if and only if $m + k < n + k$.
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a normal space. Then $(S, \tau)$ is also a Tychonoff (completely regular) space.
\end{theorem}

\begin{problem}
Prove the following indefinite integral identity:
$$ \int \frac{\mathrm{d}x}{p^2 \sin^2(ax) - q^2 \cos^2(ax)} = \frac{1}{2apq} \ln \left| \frac{p \tan(ax) - q}{p \tan(ax) + q} \right| + C $$
where $a, p, q$ are non-zero constants, and $C$ is the constant of integration.
\end{problem}

\begin{theorem}
Let $T = (S, \tau)$ be a countably compact topological space. Then $T$ is a pseudocompact topological space.
\end{theorem}

\newtheorem*{theorem}{Theorem}
\begin{theorem}
In any metric space $(A, d)$, for any subset $H \subseteq A$, if $H'$ denotes the set of limit points of $H$ and $H^i$ denotes the set of isolated points of $H$, then $H' \cap H^i = \varnothing$.
\end{theorem}

\begin{theorem}
Let $\mathbf{C}$ be a metacategory. If $f: C \to D$ is a split monomorphism in $\mathbf{C}$, then $f$ is monic.
\end{theorem}

\begin{theorem}
If $T_1$ is a compact topological space and $T_2$ is a Hausdorff space, then any continuous bijection $f: T_1 \to T_2$ is a homeomorphism.
\end{theorem}

\begin{problem}
Let $(S, \tau_p)$ be a Fortissimo space. Prove that $(S, \tau_p)$ is a completely normal space, and consequently, it satisfies all weaker separation axioms.
\end{problem}

\begin{theorem}
Let $G$ be an infinite cyclic group. Then $G$ is isomorphic to the additive group of integers: $G \cong \left(\mathbb{Z}, +\right)$.
\end{theorem}

\begin{theorem}[Nine-Point Circle Theorem]
For any triangle $\triangle ABC$, the nine points consisting of the feet of its altitudes, the midpoints of its sides, and the midpoints of the segments connecting its vertices to its orthocenter $H$, all lie on a single circle. The center $M$ of this circle lies on the Euler line of $\triangle ABC$ and is the midpoint of the segment connecting the orthocenter $H$ and the circumcenter $O$.
\end{theorem}

\begin{theorem}
The Laplace transform of the function $e^{bt} \sin(at)$ is given by the formula:
\[ \mathcal{L}\{e^{bt} \sin(at)\} = \frac{a}{(s - b)^2 + a^2}. \]
\end{theorem}

\begin{theorem}
Let $(S, \preceq)$ be an ordered set. If $f, g: S \to S$ are inflationary mappings, then their composition $f \circ g$ is also inflationary.
\end{theorem}

Prove the following identity:
\begin{align*}
\sin n \theta &= \sin \theta \left( \left( 2 \cos \theta \right)^{n - 1} - \binom {n - 2} 1 \left( 2 \cos \theta \right)^{n - 3} + \binom {n - 3} 2 \left( 2 \cos \theta \right)^{n - 5} - \cdots \right) \\
&= \sin \theta \sum_{k \ge 0} (-1)^k \binom {n - k - 1} k \left( 2 \cos \theta \right)^{n - 2k - 1}
\end{align*}

\newtheorem*{theorem}{Theorem}
\begin{theorem}
For any positive real number $r$, the sequence of real numbers $(x_n)$ defined by $x_n = \frac{1}{n^r}$ is a null sequence.
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a topological space. If every convergent sequence in $(S, \tau)$ has a unique limit point, then $(S, \tau)$ is not necessarily a Hausdorff space.
\end{theorem}

\begin{theorem}
Let $T = (S, \tau_p)$ be a Fort space on an infinite set $S$. Then $T$ is a sequentially compact space.
\end{theorem}

\begin{theorem}
Let $S$ be a set, and let $\left(\mathcal{N}_x\right)_{x \in S}$ be an indexed family such that for each $x \in S$, $\mathcal{N}_x$ is a non-empty collection of subsets of $S$.
Suppose that for each $x \in S$, the collection $\mathcal{N}_x$ satisfies the following conditions:
\begin{enumerate}
    \item[(N1)] For every $U \in \mathcal{N}_x$, $x \in U$.
    \item[(N2)] For every $U \in \mathcal{N}_x$ and every $y \in U$, there exists some $V \in \mathcal{N}_y$ such that $V \subseteq U$.
    \item[(N3)] For any $U_1, U_2 \in \mathcal{N}_x$, there exists some $U \in \mathcal{N}_x$ such that $U \subseteq U_1 \cap U_2$.
\end{enumerate}
Then the pair $(S, \tau)$ is a topological space, where $\tau = \left\{ \bigcup \mathcal{G} : \mathcal{G} \subseteq \bigcup_{x \in S} \mathcal{N}_x \right\}$.
Moreover, the family $\left(\mathcal{N}_x\right)_{x \in S}$ is a neighborhood system for $(S, \tau)$.
\end{theorem}

\noindent\textbf{Theorem (Euclid's Proposition I.29).} If a transversal line intersects two parallel lines, then:
\begin{enumerate}
    \item The alternate interior angles are equal.
    \item The corresponding angles are equal.
    \item The consecutive interior angles (also known as same-side interior angles) are supplementary, meaning their sum is equal to two right angles ($180^\circ$).
\end{enumerate}

\begin{theorem}
Let $(S, \preceq)$ be a meet semilattice. The meet operation $f(s, t) = s \wedge t$ defines an order-preserving function $f: (S \times S, \precsim) \to (S, \preceq)$, where $\precsim$ is the product order on $S \times S$ defined by $(s_1, t_1) \precsim (s_2, t_2)$ if and only if $s_1 \preceq s_2$ and $t_1 \preceq t_2$.
\end{theorem}

\begin{theorem}
Let $M_1, M_2, M_3$ be metric spaces. If $f: M_1 \to M_2$ and $g: M_2 \to M_3$ are continuous mappings, then their composition $g \circ f: M_1 \to M_3$ is continuous.
\end{theorem}

The arcsine function has a Taylor series expansion given by
\begin{align*}
\arcsin x &= \sum_{n=0}^\infty \frac{(2n)!}{2^{2n}(n!)^2} \frac{x^{2n+1}}{2n+1} \\
&= x + \frac{1}{2} \frac{x^3}{3} + \frac{1 \cdot 3}{2 \cdot 4} \frac{x^5}{5} + \frac{1 \cdot 3 \cdot 5}{2 \cdot 4 \cdot 6} \frac{x^7}{7} + \cdots
\end{align*}
This series converges for $-1 \le x \le 1$.

Let $S$ be the sequence of positive integers that can be expressed as the sum of three cubes of positive integers in two or more distinct ways. The first term of $S$ is $251$, which satisfies the property:
\begin{equation*}
251 = 5^3 + 5^3 + 1^3 = 6^3 + 3^3 + 2^3
\end{equation*}

\begin{theorem}
The set $\Bbb I$ of all basic URM instructions is countably infinite.
\end{theorem}

\begin{theorem}
Let $(D, +, \times)$ be an ordered integral domain, and let $P$ be its set of positive elements. If a binary relation $<$ is defined on $D$ such that for all $a, b \in D$,
\[ a < b \iff -a + b \in P \]
then the relation $<$ is asymmetric and antireflexive.
\end{theorem}

\begin{theorem}
Let $f$ be a real-valued function continuous on an open interval $(a, \infty)$ for some $a \in \mathbb{R}$, such that $\displaystyle \lim_{x \to \infty} f(x) = \infty$. Let $g$ be a real-valued function defined on an open interval $(b, \infty)$ for some $b \in \mathbb{R}$. If $g(x) > f(x)$ for sufficiently large $x$, then $\displaystyle \lim_{x \to \infty} g(x) = \infty$.
\end{theorem}

\begin{theorem}
In a non-Archimedean normed space $(R, \norm{\cdot}_p)$, for any elements $x, y, z \in R$, the strong triangle inequality holds:
\[
\norm{x - y}_p \le \max \set{\norm{x - z}_p, \norm{y - z}_p}
\]
\end{theorem}

\begin{theorem}
The logical disjunction operation is associative. This means that for any logical propositions $P$, $Q$, and $R$, the grouping of terms connected by disjunction does not affect the truth value of the compound proposition. Specifically, the following logical equivalences hold:
\begin{align*}
(P \lor Q) \lor R &\iff P \lor (Q \lor R) \\
P \lor (Q \lor R) &\iff (P \lor Q) \lor R
\end{align*}
\end{theorem}

\begin{theorem}
For all integers $m, n \in \Z_{> 2}$, $m \divides n$ if and only if $F_m \divides F_n$.
\end{theorem}

Prove that the cosine function $\cos x$ can be expressed piecewise as follows:
\begin{equation*}
    \cos x =
    \begin{cases}
        \sqrt{1 - \sin^2 x} & \text{if there exists an integer } n \text{ such that } \left(2n - \frac{1}{2}\right)\pi < x < \left(2n + \frac{1}{2}\right)\pi \\
        -\sqrt{1 - \sin^2 x} & \text{if there exists an integer } n \text{ such that } \left(2n + \frac{1}{2}\right)\pi < x < \left(2n + \frac{3}{2}\right)\pi
    \end{cases}
\end{equation*}

\begin{theorem}
Let $(S, \tau)$ be a topological space. If a sequence $(x_n)_{n \in \mathbb{N}}$ in $S$ converges to a limit $l \in S$, then any subsequence $(x_{n_r})_{r \in \mathbb{N}}$ of $(x_n)$ also converges to $l$. That is, if $\lim_{n \to \infty} x_n = l$, then $\lim_{r \to \infty} x_{n_r} = l$. In other words, the limit of a convergent sequence in a topological space is equal to the limit of any of its subsequences.
\end{theorem}

\begin{theorem}
The limit of the function $\frac{\cos x - 1}{x}$ as $x$ approaches $0$ is equal to $0$.
That is,
\[ \lim_{x \to 0} \frac{\cos x - 1}{x} = 0 \]
\end{theorem}

\begin{theorem}
For real constants $a$ and $p$ such that $a^2 + p^2 \neq 0$, the indefinite integral of the product of the hyperbolic sine function $\sinh(ax)$ and the sine function $\sin(px)$ is given by
$$ \int \sinh(ax) \sin(px) \, \mathrm{d}x = \frac{a \cosh(ax) \sin(px) - p \sinh(ax) \cos(px)}{a^2 + p^2} + C $$
where $C$ is the constant of integration.
\end{theorem}

\begin{theorem}
For any propositions $p$ and $q$, the biconditional statement $p \iff q$ is logically equivalent to $q \iff p$.
\end{theorem}

\begin{theorem}
Let $S_n$ be the symmetric group on $n$ letters. For any permutation $\pi \in S_n$ and any element $i \in \{1, 2, \ldots, n\}$, if $k$ is the smallest positive integer such that $\pi^k(i) \in \{i, \pi(i), \pi^2(i), \ldots, \pi^{k-1}(i)\}$, then $\pi^k(i) = i$.
\end{theorem}

\begin{theorem}
Let $\mathbb{R}$ denote the set of real numbers. Consider the metric space $(\mathbb{R}, d_1)$ where $d_1$ is the Euclidean metric on $\mathbb{R}$, and the metric space $(\mathbb{R}, d_2)$ where $d_2$ is the standard discrete metric on $\mathbb{R}$. If $f \colon (\mathbb{R}, d_2) \to (\mathbb{R}, d_1)$ is any real-valued function, then $f$ is continuous from $(\mathbb{R}, d_2)$ to $(\mathbb{R}, d_1)$.
\end{theorem}

\begin{theorem}
Let $(S, \wedge, \vee, \preceq)$ be a uniquely complemented lattice. Then the following conditions are equivalent:
\begin{enumerate}
    \item For all $p, q \in S$, $\neg p \vee \neg q = \neg (p \wedge q)$.
    \item For all $p, q \in S$, $\neg p \wedge \neg q = \neg (p \vee q)$.
    \item For all $p, q \in S$, $p \preceq q \iff \neg q \preceq \neg p$.
    \item The lattice $(S, \wedge, \vee, \preceq)$ is distributive.
\end{enumerate}
\end{theorem}

\begin{theorem}
An absolutely continuous function $f: I \to \mathbb{R}$ defined on a real interval $I \subseteq \mathbb{R}$ is continuous on $I$.
\end{theorem}

\begin{theorem}
Let $\left(X, \Sigma\right)$ be a measurable space. If $f, g: X \to \overline{\R}$ are $\Sigma$-measurable functions, then their pointwise maximum $\max \left(f, g\right): X \to \overline{\R}$ is also $\Sigma$-measurable.
\end{theorem}

\begin{theorem}
Let $(S, \circ)$ be an algebraic structure and $(T, \oplus)$ be a commutative semigroup.
Let $T^S$ denote the set of all mappings from $S$ to $T$.
If $f \colon S \to T$ and $g \colon S \to T$ are homomorphisms from $(S, \circ)$ to $(T, \oplus)$, and we define an operation $\oplus'$ on $T^S$ pointwise such that for all $x \in S$, $(f \oplus' g)(x) = f(x) \oplus g(x)$, then $f \oplus' g$ is also a homomorphism from $(S, \circ)$ to $(T, \oplus)$.
\end{theorem}

\begin{theorem}
Let $T_A = (S_A, \tau_A)$ and $T_B = (S_B, \tau_B)$ be topological spaces, and let $\phi: T_A \to T_B$ be a continuous surjection. If $T_A$ possesses any of the following properties, then $T_B$ also possesses that property:
\begin{enumerate}
    \item Compactness
    \item $\sigma$-Compactness
    \item Countable compactness
    \item Sequential compactness
    \item The Lindelöf property
\end{enumerate}
\end{theorem}

\begin{theorem}
For any integer $x$, $x$ is even if and only if $x^2 \equiv 0 \pmod 4$, and $x$ is odd if and only if $x^2 \equiv 1 \pmod 4$.
\end{theorem}

\begin{theorem}
The number of odd entries in any row of Pascal's triangle is a power of $2$.
\end{theorem}

\begin{theorem}
Let $f$ be a real function which is continuous on the closed interval $[a, b]$. Then $f$ attains a maximum value and a minimum value on $[a, b]$.
\end{theorem}

\begin{theorem}
Let $n$ be a positive integer. If $b(n)$ denotes the number of ways $n$ can be partitioned into powers of $2$, then $b(2n) = b(2n+1)$.
\end{theorem}

\begin{theorem}
Let $G$ be an abelian group with identity element $e$. For any integer $n$, the set $G^n = \{x^n : x \in G\}$ is a subgroup of $G$. Furthermore, if $G$ is finite and $n \ne 1$ is a divisor of the order of $G$, then $G^n$ is a proper subgroup of $G$.
\end{theorem}

\begin{theorem}
The dot product is distributive over vector addition. Specifically, for any vectors $\mathbf u$, $\mathbf v$, and $\mathbf w$, it holds that $(\mathbf u + \mathbf v) \cdot \mathbf w = \mathbf u \cdot \mathbf w + \mathbf v \cdot \mathbf w$.
\end{theorem}

\begin{theorem}
Let $S$ be a set, and let $\mathcal{P}(S)$ denote its power set. Let $\mathcal{B}$ be a subset of $\mathcal{P}(S)$.
Then the collection $\mathcal{F} = \{V \subseteq S : \exists U \in \mathcal{B} \text{ such that } U \subseteq V\}$ is a filter on $S$ if and only if the following two conditions hold:
\begin{enumerate}
    \item For all $V_1, V_2 \in \mathcal{B}$, there exists $U \in \mathcal{B}$ such that $U \subseteq V_1 \cap V_2$.
    \item The empty set $\emptyset \notin \mathcal{B}$ and $\mathcal{B} \ne \emptyset$.
\end{enumerate}
In this context, $\mathcal{B}$ is called a filter basis for the filter $\mathcal{F}$ generated by $\mathcal{B}$.
\end{theorem}

\begin{theorem}
If $a, b, c, d \in \mathbb{Z}$ such that $a, c \ne 0$, and $a \divides b$ and $c \divides d$, then $ac \divides bd$.
\end{theorem}

\begin{theorem}
Let $(A, d)$ be a metric space. Let $\tau$ be the topology induced by the metric $d$ on $A$. Then, for any subset $F \subseteq A$, $F$ is closed in the metric space $(A, d)$ if and only if $F$ is closed in the topological space $(A, \tau)$.
\end{theorem}

The fraction $\frac{1}{2}$ can be expressed in exactly $12$ ways as a pandigital fraction, where the numerator and denominator together use each digit from 1 to 9 exactly once. These expressions are:
\begin{align*}
\frac{1}{2} &= \frac{6729}{13\,458} & \frac{1}{2} &= \frac{6792}{13\,584} \\
\frac{1}{2} &= \frac{6927}{13\,854} & \frac{1}{2} &= \frac{7269}{14\,538} \\
\frac{1}{2} &= \frac{7293}{14\,586} & \frac{1}{2} &= \frac{7329}{14\,658} \\
\frac{1}{2} &= \frac{7692}{15\,384} & \frac{1}{2} &= \frac{7923}{15\,846} \\
\frac{1}{2} &= \frac{7932}{15\,864} & \frac{1}{2} &= \frac{9267}{18\,534} \\
\frac{1}{2} &= \frac{9273}{18\,546} & \frac{1}{2} &= \frac{9327}{18\,654}
\end{align*}

\begin{theorem}
For any non-negative integer $n$, the unsigned Stirling number of the first kind, denoted by $\genfrac{[}{]}{0pt}{}{0}{n}$, is equal to the Kronecker delta $\delta_{0n}$. That is,
\[
\genfrac{[}{]}{0pt}{}{0}{n} = \delta_{0n}
\]
\end{theorem}

\begin{theorem}
Every well-ordered set is order isomorphic to a unique ordinal.
\end{theorem}

\begin{theorem}
Let $(S, \tau_p)$ be a Fort space such that $S$ is a countably infinite set. Then $(S, \tau_p)$ is a metrizable space.
\end{theorem}

\begin{theorem}
For every real number $x$, it holds that $0 \cdot x = 0$.
\end{theorem}

\begin{theorem}
A sequentially compact metric space is compact.
\end{theorem}

\begin{theorem}
The following identity holds:
\[
    \int_0^{\pi/2} \left( \ln (\cos x) \right)^2 \, dx = \frac{\pi}{2} \left( \ln 2 \right)^2 + \frac{\pi^3}{24}
\]
\end{theorem}

\begin{theorem}
If $x \in \mathbb{Z}$ is an even integer, then $x+5$ is an odd integer.
\end{theorem}

Let $S$ be a subspace of the real vector space $\R^2$. Then $S$ is one of the following:
\begin{enumerate}
    \item The entire space $\R^2$.
    \item The zero subspace, $\left\{0\right\}$.
    \item A line passing through the origin.
\end{enumerate}

\begin{theorem}[Euclid's Theorem]
For any finite set of prime numbers, there exists a prime number not in that set.
\end{theorem}

\begin{theorem}
Every complete lattice, when equipped with its order topology, is a compact topological space.
\end{theorem}

\begin{theorem}[Image Factorization]
Every function $f:S \to T$ can be uniquely factored as the composition of a surjective function $g$ and an inclusion map $i_T$. That is, $f = i_T \circ g$, where $g: S \to \operatorname{Im}(f)$ is defined by $g(x) = f(x)$ for all $x \in S$, and $i_T: \operatorname{Im}(f) \to T$ is the inclusion map defined by $i_T(x) = x$ for all $x \in \operatorname{Im}(f)$. This decomposition is illustrated by the following commutative diagram:
\[
\xymatrix@L+2mu@+1em {
 S \ar@{-->}[r]^*{g}
     \ar[rd]_*{f = i_T \circ g}
&
 \operatorname{Im}(f) \ar@{-->}[d]^*{i_T}
\\
&
 T
}
\]
\end{theorem}

\begin{theorem}
The operation of multiplication on the set of real numbers $\R$ is well-defined.
\end{theorem}

\begin{theorem}
Let $L/K$ be a field extension and let $\alpha \in L$ be algebraic over $K$. Then the minimal polynomial of $\alpha$ over $K$ is unique.
\end{theorem}

Let a circle $C_1$ of radius $b$ roll without slipping around the inside of a larger circle $C_2$ of radius $a$. Circle $C_2$ is centered at the origin $O$ of a Cartesian plane. A point $P$ on the circumference of $C_1$ starts at the point of tangency with $C_2$, which is $A = (a, 0)$ on the $x$-axis. The hypocycloid traced by point $P$ has coordinates $(x, y)$ given by the parametric equations:
\begin{align*}
x &= (a - b) \cos \theta + b \cos \left( \frac{a - b}{b} \theta \right) \\
y &= (a - b) \sin \theta - b \sin \left( \frac{a - b}{b} \theta \right)
\end{align*}

\begin{theorem}
Let $U$ be an open subset of $\mathbb{C}$. If $(f_n)_{n \in \mathbb{N}}$ is a sequence of analytic functions $f_n \colon U \to \mathbb{C}$ that converges locally uniformly to a function $f$ on $U$, then the sequence of derivatives $(f_n')_{n \in \mathbb{N}}$ converges locally uniformly to $f'$ on $U$.
\end{theorem}

\begin{theorem}
In a metric space $(A, d)$ where $d$ is the standard discrete metric, every subset of $A$ is a neighborhood of each of its points.
\end{theorem}

The special case of Legendre's differential equation,
\begin{equation}
\left(1 - x^2\right) y'' - 2 x y' + 2 y = 0,
\end{equation}
has the general solution
\begin{equation}
y = C_1 x + C_2 \left( \frac x 2 \ln \left( \frac{1 + x}{1 - x} \right) - 1 \right).
\end{equation}

\begin{theorem}
Let $(G, \circ)$ be an abelian group. If $H_1$ and $H_2$ are subgroups of $G$, then $H_1 \circ H_2$ is a subgroup of $G$.
\end{theorem}

\begin{theorem}
Let $T$ be a topological space. Let $(f_t)_{t \in T}$ be a centered (mean zero) Gaussian process on $T$. Assume that its supremum norm, defined as $\left\|f\right\|_T := \sup_{t \in T} |f_t|$, is almost surely finite. Let $\sigma_T^2 := \sup_{t \in T} \operatorname E[|f_t|^2]$. Then $\operatorname E[\left\|f\right\|_T]$ and $\sigma_T$ are both finite, and for each $u > 0$, the following concentration inequality holds:
\[
\operatorname P\left(\left\|f\right\|_T > \operatorname E[\left\|f\right\|_T] + u\right) \le \exp\left(\frac{-u^2}{2 \sigma_T^2}\right)
\]
\end{theorem}

The distance between two points $A=(x_1, y_1)$ and $B=(x_2, y_2)$ in the complex plane is given by the formula:
\begin{align*}
|AB| &= \sqrt{\left(x_2 - x_1\right)^2 + \left(y_2 - y_1\right)^2} \\
&= \left|z_1 - z_2\right|
\end{align*}
where $z_1 = x_1 + iy_1$ and $z_2 = x_2 + iy_2$ are the complex numbers representing $A$ and $B$, respectively.

\begin{theorem}
The limit of the difference of two convergent sequences is the difference of their limits. Specifically, if $(x_n)$ and $(y_n)$ are sequences such that $\lim_{n \to \infty} x_n = l$ and $\lim_{n \to \infty} y_n = m$, then
\[
\lim_{n \to \infty} (x_n - y_n) = l - m.
\]
\end{theorem}

\begin{theorem}
For any positive number $x$, the natural logarithm, denoted as $\ln x$ (base $e$), and the common logarithm, denoted as $\log_{10} x$ (base 10), are related by the change of base formula, which can be expressed in the following two equivalent forms:
\begin{enumerate}
    \item $\displaystyle \ln x = \frac{\log_{10} x}{\log_{10} e}$
    \item $\displaystyle \log_{10} x = \frac{\ln x}{\ln 10}$
\end{enumerate}
\end{theorem}

The cosine function has the power series expansion, valid for all $x \in \R$, given by
\begin{equation*}
\cos x = \sum_{n=0}^\infty (-1)^n \frac{x^{2n}}{(2n)!} = 1 - \frac{x^2}{2!} + \frac{x^4}{4!} - \frac{x^6}{6!} + \cdots
\end{equation*}

\begin{theorem}
Let $S$ and $T$ be sets, and let $\RR \subseteq S \times T$ be a relation. For any subsets $T_1$ and $T_2$ of $T$, the following equality holds:
\[
\RR^{-1} \left[ T_1 \cup T_2 \right] = \RR^{-1} \left[ T_1 \right] \cup \RR^{-1} \left[ T_2 \right]
\]
\end{theorem}

\begin{theorem}
Let $(S, \le, \tau)$ be a linearly ordered topological space, and let $F_1, F_2, F_3$ be filter bases in $S$.
Suppose that for each $T \in F_1$, $F_2$ has an element $M$ such that all elements of $M$ succeed some element of $T$ (i.e., for every $x \in M$, there exists $y \in T$ with $y \le x$).
Furthermore, suppose that for each $U \in F_3$, $F_2$ has an element $N$ such that all elements of $N$ precede some element of $U$ (i.e., for every $x \in N$, there exists $y \in U$ with $x \le y$).
If $F_1$ and $F_3$ both converge to a point $p \in S$, then $F_2$ converges to $p$.
\end{theorem}

\begin{theorem}
A locally path-connected topological space $T = (S, \tau)$ is not necessarily locally arc-connected.
\end{theorem}

\begin{theorem}
Let $A$ be a non-trivial commutative ring with unity. Then $A$ has a prime ideal.
\end{theorem}

\begin{theorem}
Let $L = (S, \vee, \wedge, \preceq)$ be a bounded below continuous lattice. Then $L$ is meet-continuous.
\end{theorem}

The second-order ordinary differential equation, given by
\begin{equation*}
y'' + 8y = 0,
\end{equation*}
possesses the general solution
\begin{equation*}
y = C_1 \cos(2\sqrt{2}x) + C_2 \sin(2\sqrt{2}x).
\end{equation*}

\begin{theorem}
Let $b \in \mathbb{Z}$ be an integer such that $b > 1$, and let $n = b^k - 1$ for some integer $k \ge 1$. Then, when both numbers are expressed in base $b$ representation, $2n$ is the number formed by reversing the digits of $(b-1)n$.
\end{theorem}

\begin{theorem}
Let $(D, +, \circ)$ be an integral domain. Then $D$ is a reduced ring.
\end{theorem}

\begin{theorem}[Peirce's Law]
  The propositional formula $((P \to Q) \to P) \to P$ is a tautology.
\end{theorem}

\begin{theorem}
A repunit number, other than $1$, is never a perfect square.
\end{theorem}

In Tarski's Geometry, the two forms of Pasch's Axiom are consistent, meaning that the following two expressions are logically equivalent:
\begin{align*}
(1) \quad & \forall a, b, c, p, q: \exists x: \mathsf B a p c \land \mathsf B b q c \implies \mathsf B p x b \land \mathsf B q x a \\
(2) \quad & \forall a, b, c, p, q: \exists x: \mathsf B a p c \land \mathsf B q c b \implies \mathsf B a x q \land \mathsf B b p x
\end{align*}

\begin{theorem}
The midline of a triangle is parallel to the third side of the triangle and is half the length of that side.
\end{theorem}

\begin{theorem}
A planar graph $G$ can be assigned a proper vertex $k$-coloring such that $k \le 5$.
\end{theorem}

\begin{theorem}
For all complex numbers $z$ and integers $w$, such that $z$ is not an integer (and thus not a negative integer), the binomial coefficient $\binom{z}{w}$ satisfies the identity:
\[
\binom{z}{w} = \frac{\sin(\pi(w - z - 1))}{\sin(\pi z)} \binom{w - z - 1}{w}.
\]
\end{theorem}

\begin{theorem}
If $G$ is a group and $N$ is a normal subgroup of $G$, then the quotient group $G/N$ is a group.
\end{theorem}

\begin{theorem}
Let $S$ be a set of $n+1$ positive integers, all less than or equal to $2n$. Then there exist two distinct elements $a, b \in S$ such that $a$ divides $b$.
\end{theorem}

\begin{problem}
Prove that $p \vdash (p \implies q) \implies q$.
\end{problem}

Let $n$ be a positive integer. The following identities hold for any distinct integers $k_1, k_2, k_3 \in \{1, 2, \ldots, n\}$, where $\begin{bmatrix} i & j \end{bmatrix}$ denotes the transposition swapping $i$ and $j$:
\begin{enumerate}
    \item $\begin{bmatrix} k_1 & k_2 \end{bmatrix} = \begin{bmatrix} k_3 & k_2 \end{bmatrix} \begin{bmatrix} k_1 & k_3 \end{bmatrix} \begin{bmatrix} k_3 & k_2 \end{bmatrix}$
    \item $\begin{bmatrix} k_1 & k_2 \end{bmatrix} = \begin{bmatrix} k_3 & k_1 \end{bmatrix} \begin{bmatrix} k_3 & k_2 \end{bmatrix} \begin{bmatrix} k_3 & k_1 \end{bmatrix}$
\end{enumerate}

\begin{theorem}
For any integer $x$, it holds that $x^2 \equiv 0 \pmod 3$ or $x^2 \equiv 1 \pmod 3$.
\end{theorem}

Prove that for the real function $f(x) = x$ defined on the interval $(0, \pi)$, its half-range Fourier cosine series is given by:
\begin{align*}
x &\sim \frac{\pi}{2} - \frac{4}{\pi} \sum_{n=1}^\infty \frac{\cos((2n-1)x)}{(2n-1)^2} \\
&= \frac{\pi}{2} - \frac{4}{\pi} \left(\cos x + \frac{\cos 3x}{3^2} + \frac{\cos 5x}{5^2} + \dots\right)
\end{align*}

\begin{theorem}
Let $(S, \tau)$ be a discrete topological space. Then $(S, \tau)$ is paracompact.
\end{theorem}

\begin{theorem}
Let $(X, \preceq, \tau)$ be a linearly ordered space. Then $(X, \tau)$ is a Hausdorff space.
\end{theorem}

Prove that for all $x \in \mathbb{R}$, the Maclaurin series expansion of $e^{\sin x}$ is given by
\begin{equation*}
e^{\sin x} = 1 + x + \frac{x^2}{2} - \frac{x^4}{8} - \frac{x^5}{15} + \cdots
\end{equation*}

\begin{proposition}
It is possible to bisect a given finite straight line. (Euclid I.10)
\end{proposition}

\begin{theorem}
Let $I$ be a countable indexing set. Let $\left( S_\alpha, \tau_\alpha \right)_{\alpha \in I}$ be a family of topological spaces indexed by $I$. If each $\left( S_\alpha, \tau_\alpha \right)$ is separable, then their product space $\left( S, \tau \right) = \prod_{\alpha \in I} \left( S_\alpha, \tau_\alpha \right)$ is also separable.
\end{theorem}

For an ordered set $(S, \preceq)$, the following conditions are equivalent:
\begin{enumerate}
    \item $(S, \preceq)$ is Dedekind complete.
    \item Every non-empty subset of $S$ that is bounded below admits an infimum in $S$.
    \item Its dual ordered set is Dedekind complete.
\end{enumerate}

\begin{theorem}
The integers $11$ and $26$ cannot be represented as the sum of fewer than six hexagonal numbers.
\end{theorem}

\begin{theorem}
Let $\{(S_\alpha, \tau_\alpha)\}_{\alpha \in I}$ be an indexed family of topological spaces. Let $(S, \tau) = \prod_{\alpha \in I} (S_\alpha, \tau_\alpha)$ be their product space, and let $\operatorname{pr}_\alpha: (S, \tau) \to (S_\alpha, \tau_\alpha)$ denote the projection onto the $\alpha$-th coordinate space. If $(S, \tau)$ is paracompact, then each coordinate space $(S_\alpha, \tau_\alpha)$ is also paracompact.
\end{theorem}

\begin{theorem}
Let $\alpha$, $\beta$, and $\gamma$ be Dedekind cuts. If $\alpha + \beta$ denotes the sum of the cuts $\alpha$ and $\beta$, and $\alpha \beta$ denotes their product, then the distributive property holds:
\[ \alpha \left( \beta + \gamma \right) = \alpha \beta + \alpha \gamma \]
\end{theorem}

\begin{theorem}
Let $L = \left({S, \vee, \wedge, \preceq}\right)$ be a bounded below, up-complete lattice.
Let $\mathit{Ids}(L)$ be the set of all ideals in $L$, and consider it as an ordered set $P = (\mathit{Ids}(L), \subseteq)$ where the ordering is given by set inclusion.
If the map $f: \mathit{Ids}(L) \to S$, defined by $f(I) = \sup I$ for all $I \in \mathit{Ids}(L)$, is an upper adjoint of a Galois connection, then $L$ is a continuous ordered set.
\end{theorem}

The only two square numbers which are $4$ less than a perfect cube are $2^2$ and $11^2$, as demonstrated by the following equations:
\begin{align*}
2^2 + 4 &= 2^3 \\
11^2 + 4 &= 5^3
\end{align*}

Let $S$ and $T$ be sets. Then at least one of the following conditions holds:
\begin{enumerate}
    \item There exists an injective mapping $f: S \to T$.
    \item There exists a surjective mapping $f: S \to T$.
\end{enumerate}

\begin{theorem}
Let $\left(S, \tau\right)$ be an indiscrete topological space. Then $\left(S, \tau\right)$ is hereditarily compact.
\end{theorem}

\begin{theorem}
In a domain $R$, the only idempotent elements are the additive identity $0$ and the multiplicative identity $1$. That is, for any $x \in R$, $x^2 = x$ if and only if $x = 0$ or $x = 1$.
\end{theorem}

\begin{theorem}
Let $K$ be an ellipse with foci $F_1$ and $F_2$. For any point $P$ on $K$, let $d_1$ be the distance from $P$ to $F_1$ and $d_2$ be the distance from $P$ to $F_2$. The sum $d = d_1 + d_2$ is a constant value for all points $P$ on $K$, and this constant $d$ is equal to the length of the major axis of $K$.
\end{theorem}

The Riemann zeta function $\zeta(s)$ for even positive integers $s=2n$ is given by the following identity, relating its series definition to a closed-form expression involving Bernoulli numbers:
\begin{align*}
\zeta(2n) &= (-1)^{n+1} \frac{B_{2n} 2^{2n-1} \pi^{2n}}{(2n)!} \\
&= \frac{1}{1^{2n}} + \frac{1}{2^{2n}} + \frac{1}{3^{2n}} + \frac{1}{4^{2n}} + \cdots
\end{align*}
where $B_n$ denotes the $n$-th Bernoulli number and $n$ is a positive integer.

\begin{theorem}
For any natural numbers $a$ and $b$, if $a$ and $b$ are cube numbers, then their product $ab$ is also a cube number. (Euclid, Proposition IX.4)
\end{theorem}

\begin{theorem}
Let $(S, \preceq, \tau)$ be an ordered set. Let $T$ be a subset of $S$. Let $U$ be the upper closure of $T$. Then $U$ is an upper set.
\end{theorem}

\begin{theorem}
Let $a, b, c$ be real numbers such that $a \neq 0$ and $b^2 - 4ac \neq 0$. Then, the following integral identity holds:
$$ \int \frac{x \, dx}{\left(\sqrt{ax^2 + bx + c}\right)^3} = \frac{2(bx + 2c)}{(b^2 - 4ac)\sqrt{ax^2 + bx + c}} $$
\end{theorem}

\begin{theorem}[Euclid III.26]
In equal circles, equal angles stand on equal arcs, whether at the center or at the circumference of those circles.
\end{theorem}

\begin{theorem}
If a subset $S$ of the positive integers $\mathbb{N}_{>0}$ satisfies the conditions that $1 \in S$ and for all $n \in \mathbb{N}_{>0}$, if $n \in S$ then $n+1 \in S$, then $S = \mathbb{N}_{>0}$.
\end{theorem}

\begin{theorem}[Arithmetic Mean-Geometric Mean Inequality]
Let $x_1, x_2, \ldots, x_n$ be $n$ positive real numbers. Let $A_n$ denote their arithmetic mean and $G_n$ their geometric mean. Then, $A_n \ge G_n$. Equality holds if and only if all the numbers are equal, i.e., $x_1 = x_2 = \cdots = x_n$.
\end{theorem}

\begin{theorem}
It is possible to construct a regular polygon with $257$ sides using a compass and straightedge construction.
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a topological space. If $(S, \tau)$ is a $T_4$ space and also a $T_3$ space, then it is a $T_{3 \frac{1}{2}}$ space.
\end{theorem}

\begin{theorem}
Let $G$ be a group. If $N$ is a normal subgroup of $G$ and $K$ is a normal subgroup of $N$, then $K$ is not necessarily a normal subgroup of $G$.
\end{theorem}

The operation of matrix addition, defined entrywise for any two $m \times n$ matrices $\mathbf{A}$ and $\mathbf{B}$ with entries from a field $\mathbb{F}$ (denoted $\mathrm{M}_{m,n}(\mathbb{F})$), is commutative. That is, for all $\mathbf{A}, \mathbf{B} \in \mathrm{M}_{m,n}(\mathbb{F})$, we have
\begin{equation*}
\mathbf{A} + \mathbf{B} = \mathbf{B} + \mathbf{A}.
\end{equation*}

\begin{theorem}
If a relation $\mathcal R$ on a set $S$ is reflexive and circular, then $\mathcal R$ is an equivalence relation.
\end{theorem}

\begin{theorem}
Let $p, q \in \Z_{>0}$ be coprime integers. Considering $\Z/p\Z$ and $\Z/q\Z$ as $\Z$-modules, their tensor product over $\Z$ is zero:
\[ \Z/p\Z \otimes_\Z \Z/q\Z = 0. \]
\end{theorem}

 egin{theorem}
If there exist non-zero integers $x, y, z$ and an odd prime $p$ such that $x^p + y^p = z^p$ (Fermat's equation) and $p 
mid x$, $p 
mid y$, $p 
mid z$, then $p^2 \mid (2^{p-1} - 1)$.
\end{theorem}

\begin{theorem}
Let $S$ be a set and $\mathcal{R}$ be an equivalence relation on $S$. Let $S/\mathcal{R}$ denote the quotient set of $S$ by $\mathcal{R}$. If $\mathcal{R}'$ is the relation on $S$ induced by the partition $S/\mathcal{R}$, then $\mathcal{R}' = \mathcal{R}$.
\end{theorem}

\begin{theorem}
All transpositions are self-inverse.
\end{theorem}

\begin{theorem}
Let $f: [a,b] \to \mathbb{R}$ be a function that is continuous on the closed interval $[a,b]$ and differentiable on the open interval $(a,b)$. If $f'(x) = 0$ for all $x \in (a,b)$, then $f$ is constant on $[a,b]$.
\end{theorem}

\begin{theorem}
Let $T = (S, \tau)$ be the Arens-Fort space. Then $T$ is a paracompact space.
\end{theorem}

\begin{theorem}
Let $(G_1, \circ_1)$ and $(G_2, \circ_2)$ be groups. If $(G, \circ)$ is the group direct product of $(G_1, \circ_1)$ and $(G_2, \circ_2)$, then the first projection $\pr_1: (G, \circ) \to (G_1, \circ_1)$ and the second projection $\pr_2: (G, \circ) \to (G_2, \circ_2)$ are both epimorphisms.
\end{theorem}

\begin{theorem}[Euclid, Elements, Book XI, Proposition 35, Porism]
All solid parallelepipeds which are on the same base and of the same height are equal to one another.
\end{theorem}

\begin{theorem}
Let $(S, \tau_S)$ be a topological space, $(G, *, \tau_G)$ be a topological group, and $\lambda \in G$. If $f, g \colon (S, \tau_S) \to (G, \tau_G)$ are continuous mappings, then the following hold:
\begin{enumerate}
    \item \textbf{Product Rule:} The product mapping $f * g \colon S \to G$, defined by $(f * g)(x) = f(x) * g(x)$ for all $x \in S$, is continuous.
    \item \textbf{Multiple Rule:} The left and right translation mappings $\lambda * f \colon S \to G$ and $f * \lambda \colon S \to G$, defined by $(\lambda * f)(x) = \lambda * f(x)$ and $(f * \lambda)(x) = f(x) * \lambda$ for all $x \in S$, are continuous.
    \item \textbf{Inverse Rule:} The inverse mapping $f^{-1} \colon S \to G$, defined by $f^{-1}(x) = (f(x))^{-1}$ for all $x \in S$, is continuous.
\end{enumerate}
\end{theorem}

Let $n \in \mathbb{Z}_{>0}$ be a positive integer. Let $(\mathbb{C}_{
eq 0}, 	imes)$ be the multiplicative group of non-zero complex numbers. The function $f_n \colon \mathbb{C}_{
eq 0} 	o \mathbb{C}_{
eq 0}$ defined by $f_n(z) = z^n$ for all $z \in \mathbb{C}_{
eq 0}$ is a group epimorphism from $(\mathbb{C}_{
eq 0}, 	imes)$ to $(\mathbb{C}_{
eq 0}, 	imes)$, and its kernel, $\operatorname{Ker}(f_n)$, is the set of complex $n$-th roots of unity.

Theorem: For real numbers $a, b, c$ such that $a > 0$, the following identity holds:
\begin{equation*}
    \int_0^\infty e^{-(a x^2 + b x + c)} \mathrm{d} x = \frac{1}{2} \sqrt{\frac{\pi}{a}} e^{\frac{b^2 - 4 a c}{4 a}} \erfc\left(\frac{b}{2 \sqrt{a}}\right)
\end{equation*}

The triple angle formulas for sine, cosine, and tangent are given by the following identities, valid for all angles $\theta$ for which the expressions are defined:
\begin{align*}
\sin(3\theta) &= 3\sin\theta - 4\sin^3\theta \\
\cos(3\theta) &= 4\cos^3\theta - 3\cos\theta \\
\tan(3\theta) &= \frac{3\tan\theta - \tan^3\theta}{1 - 3\tan^2\theta}
\end{align*}

\begin{theorem}
Let $(X, \Sigma)$ and $(X', \Sigma')$ be measurable spaces. Let $\mu$ be a measure on $(X, \Sigma)$, and let $f: X \to X'$ be a $\Sigma/\Sigma'$-measurable mapping. Then the pushforward measure $f_* \mu: \Sigma' \to \overline{\mathbb{R}}$ is a measure.
\end{theorem}

\begin{theorem}
Let $f$ be a real-valued function defined on a closed interval $[a, b]$. If $f$ is piecewise continuous on $[a, b]$, then $f$ is Darboux integrable on $[a, b]$.
\end{theorem}

Let $\mathbf a, \mathbf b, \mathbf c, \mathbf d$ be vectors in a three-dimensional vector space $\mathbf V$ with a standard ordered basis $\left( \mathbf e_1, \mathbf e_2, \mathbf e_3 \right)$. If the vector cross product is denoted by $\times$ and the dot product by $\cdot$, and the vectors are expressed as:
\begin{align*}
\mathbf a &= a_1 \mathbf e_1 + a_2 \mathbf e_2 + a_3 \mathbf e_3 \\
\mathbf b &= b_1 \mathbf e_1 + b_2 \mathbf e_2 + b_3 \mathbf e_3 \\
\mathbf c &= c_1 \mathbf e_1 + c_2 \mathbf e_2 + c_3 \mathbf e_3 \\
\mathbf d &= d_1 \mathbf e_1 + d_2 \mathbf e_2 + d_3 \mathbf e_3
\end{align*}
then prove the following identity:
\begin{align*}
\left( \mathbf a \times \mathbf b \right) \times \left( \mathbf c \times \mathbf d \right) &= \mathbf c \left( \mathbf a \cdot \left( \mathbf b \times \mathbf d \right) \right) - \mathbf d \left( \mathbf a \cdot \left( \mathbf b \times \mathbf c \right) \right) \\
&= \mathbf b \left( \mathbf a \cdot \left( \mathbf c \times \mathbf d \right) \right) - \mathbf a \left( \mathbf b \cdot \left( \mathbf c \times \mathbf d \right) \right)
\end{align*}

\begin{theorem}
Every metric space is a completely normal space.
\end{theorem}

\begin{theorem}
Let $(a_n)$ be a sequence in a field $\mathbb{K} \in \{\mathbb{Q}, \mathbb{R}, \mathbb{C}\}$. If the series $\displaystyle \sum_{n=1}^\infty a_n$ converges in $\mathbb{K}$, then $\lim_{n \to \infty} a_n = 0$.
\end{theorem}

Let $n \in \mathbb{Z}$ be a positive integer that is not a perfect square. Then the continued fraction expansion of $\sqrt{n}$ is periodic. If $a_1 = \lfloor \sqrt{n} \rfloor$ is the first partial quotient, then the expansion takes one of the following two forms, where $m \ge 0$ is an integer:
\begin{align*}
\sqrt{n} &= [a_1; \overline{b_1, b_2, \ldots, b_{m-1}, b_m, b_{m-1}, \ldots, b_2, b_1, 2a_1}] \\
\intertext{or}
\sqrt{n} &= [a_1; \overline{b_1, b_2, \ldots, b_{m-1}, b_m, b_m, b_{m-1}, \ldots, b_2, b_1, 2a_1}]
\end{align*}
In both cases, the repeating part (or period) starts with a palindromic sequence of partial quotients (which may be of length zero, corresponding to $m=0$), and ends with $2a_1$, which is twice the first partial quotient.

\begin{theorem}
Let $X$ be a Riemann surface. Let $\Gamma_1$ and $\Gamma_2$ be families of rectifiable curves (or disjoint unions of rectifiable curves) on $X$. Assume that $\Gamma_1$ and $\Gamma_2$ are separated by disjoint Borel subsets $A_1, A_2 \subseteq X$, such that every curve in $\Gamma_1$ is contained in $A_1$ and every curve in $\Gamma_2$ is contained in $A_2$. Let $\Gamma$ be a third curve family such that every curve in $\Gamma_1 \cup \Gamma_2$ contains a subcurve that is an element of $\Gamma$. Then the extremal length of $\Gamma$ satisfies
\[ \frac{1}{\lambda(\Gamma)} \ge \frac{1}{\lambda(\Gamma_1)} + \frac{1}{\lambda(\Gamma_2)}. \]
\end{theorem}

\begin{theorem}
If a body $B$ starts from rest and is subjected to a constant acceleration, then the distance traveled by $B$ is proportional to the square of the time for which $B$ is under acceleration.
\end{theorem}

\begin{theorem}
    For any perfect number $n$ and any positive integer $m > 1$, the product $mn$ is an abundant number.
\end{theorem}

\begin{theorem}
If a metric space $(A, d)$ is compact, then it is totally bounded.
\end{theorem}

\begin{theorem}
Let $S_n$ denote the symmetric group on $n$ letters. Every element of $S_n$ can be expressed as a product of transpositions.
\end{theorem}

\begin{theorem}
Let $\mathbf C$ be a metacategory. If $1$ and $1'$ are two terminal objects in $\mathbf C$, then there exists a unique isomorphism $u: 1 \to 1'$. Consequently, terminal objects are unique up to unique isomorphism.
\end{theorem}

\begin{theorem}
The external direct product of a finite sequence of groups is itself a group.
\end{theorem}

Prove the following identity, where $C$ denotes the constant of integration:
\begin{align*}
\int \frac{\arccsc\left(\frac{x}{a}\right)}{x} \, dx &= -\left( \sum_{k=0}^{\infty} \frac{(2k+1)!}{2^{2k} (k!)^2 (2k+1)^3} \left(\frac{a}{x}\right)^{2k+1} \right) + C \\
&= -\left( \frac{a}{x} + \frac{1}{2 \cdot 3^2} \left(\frac{a}{x}\right)^3 + \frac{1 \cdot 3}{2 \cdot 4 \cdot 5^2} \left(\frac{a}{x}\right)^5 + \frac{1 \cdot 3 \cdot 5}{2 \cdot 4 \cdot 6 \cdot 7^2} \left(\frac{a}{x}\right)^7 + \cdots \right) + C
\end{align*}

\begin{theorem}
Let $H$ and $K$ be Hilbert spaces. If $T: H \to K$ is a bounded finite rank operator, then $T$ is a compact operator.
\end{theorem}

\begin{theorem}[Pappus's Centroid Theorem]
Pappus's Centroid Theorem comprises two fundamental statements regarding surfaces and solids of revolution:
\begin{enumerate}
    \item \textbf{Pappus's Centroid Theorem for Surface Area (Pappus's Second Theorem):} The surface area $A$ of a surface of revolution, generated by revolving a plane curve $C$ about an external axis, is equal to the product of the length of the curve $L$ and the distance $d$ traveled by the centroid of the curve. This is expressed as $A = L \cdot (2\pi \bar{r})$, where $\bar{r}$ denotes the perpendicular distance from the centroid of the curve to the axis of revolution.
    \item \textbf{Pappus's Centroid Theorem for Volume (Pappus's First Theorem):} The volume $V$ of a solid of revolution, generated by revolving a plane region $R$ about an external axis, is equal to the product of the area of the region $A$ and the distance $d$ traveled by the centroid of the region. This is expressed as $V = A \cdot (2\pi \bar{r})$, where $\bar{r}$ denotes the perpendicular distance from the centroid of the region to the axis of revolution.
\end{enumerate}
\end{theorem}

\begin{theorem}
Let $\N$ be the set of non-negative integers. For all $m, n \in \N$, the product $m \times n$ is equal to $0$ if and only if $m = 0$ or $n = 0$. Consequently, $\N$ contains no proper zero divisors.
\end{theorem}

\begin{proposition}
Let $S$ be a set, and let $\alpha$ be a relation on $S$. If $\alpha$ is symmetric and transitive, then $\alpha$ is not necessarily reflexive.
\end{proposition}

Let $\R_{
e 0}$ be the set of all non-zero real numbers, formally defined as $\R_{
e 0} = \R \setminus \{0\}$. The algebraic structure $(\R_{
e 0}, 	imes)$, where $	imes$ denotes standard multiplication, is an uncountable abelian group.

\begin{theorem}
Let $G$ be a finite group of order $n > 1$. If $p$ is the least prime divisor of $n$ and $H$ is a subgroup of $G$ of index $p$, then $H$ is normal in $G$.
\end{theorem}

Show that the particular solution to the second-order ordinary differential equation
\begin{equation*}
y y'' = y^2 y' + (y')^2
\end{equation*}
subject to the initial conditions $y(0) = -\frac{1}{2}$ and $y'(0) = 1$ is
\begin{equation*}
2y - 3 = 8y \exp\left(\frac{3x}{2}\right).
\end{equation*}

\begin{theorem}
For any real numbers $a, b, c$, if $a < b$ and $c > 0$, then $ac < bc$.
\end{theorem}

\begin{theorem}
There are infinitely many prime numbers.
\end{theorem}

Let $A$ be a set, and let $G$ be a function. Consider a function $F \colon \operatorname{On} \to V$ (where $V$ is the class of all sets) defined by transfinite recursion such that for every ordinal $x$, $F(x) = G(F \restriction x)$. Assume further that for every ordinal $x$, if $A \setminus F[x] \ne \varnothing$, then $G(F \restriction x) \in A \setminus F[x]$, where $F[x]$ denotes the image of the set $x$ under $F$. Prove that there exists an ordinal $y$ satisfying the following properties:
\begin{enumerate}
    \item For all ordinals $x < y$, $A \setminus F[x] \ne \varnothing$.
    \item $F[y] = A$.
    \item $F \restriction y$ is an injective map.
\end{enumerate}

\begin{theorem}
The countable complement topology on any set $S$ is a topology.
\end{theorem}

\begin{theorem}
The additive identity for the addition of rational numbers is $0$, which formally states that:
$$ \exists 0 \in \Q: \forall a \in \Q: a + 0 = a = 0 + a $$
\end{theorem}

\begin{theorem}
If $(X, \Sigma)$ is a measurable space and $f: X \to \mathbb{R}$ is a simple function, then its absolute value, $|f|: X \to \mathbb{R}$, is also a simple function.
\end{theorem}

Consider a hypocycloid defined by the parametric equations:
\begin{align*}
x &= (a - b) \cos \theta + b \cos \left( \frac{a - b}{b} \theta \right) \\
y &= (a - b) \sin \theta - b \sin \left( \frac{a - b}{b} \theta \right)
\end{align*}
Show that by replacing $b$ with $-b$ in these equations, one obtains the parametric equations for an epicycloid:
\begin{align*}
x &= (a + b) \cos \theta - b \cos \left( \frac{a + b}{b} \theta \right) \\
y &= (a + b) \sin \theta - b \sin \left( \frac{a + b}{b} \theta \right)
\end{align*}

\begin{theorem}
The standard discrete metric is a metric.
\end{theorem}

\begin{theorem}
For any two sets $A$ and $B$, the characteristic function of their union, denoted $\chi_{A \cup B}$, satisfies the identity:
\[ \chi_{A \cup B} = \min \left\{ \chi_A + \chi_B, 1 \right\}. \]
\end{theorem}

\begin{theorem}
In a $T_1$ topological space $(S, \tau)$, any connected subset $H \subseteq S$ with more than one element is dense-in-itself.
\end{theorem}

\begin{theorem}
Let $(S, \preceq)$ and $(T, \preccurlyeq)$ be totally ordered sets. Suppose $f \colon S \to T$ and $g \colon S \to T$ are increasing mappings. If there exists a subset $H \subseteq S$ such that $f(x) = g(x)$ for all $x \in H$, and the image $f(H)$ is dense in $T$ (meaning that for any $u, v \in T$ with $u < v$, the open interval $(u, v)$ has a non-empty intersection with $f(H)$), then $f=g$.
\end{theorem}

\begin{theorem}
Let $G$ be a topological group and $H$ a subgroup of $G$. Let $G/H$ denote the quotient space of left cosets. Then the following conditions are equivalent:
\begin{enumerate}
    \item $H$ is closed in $G$.
    \item $G/H$ is Hausdorff.
\end{enumerate}
\end{theorem}

Let $f: D \to \mathbb{C}$ be an analytic function defined on an open set $D \subset \mathbb{C}$ containing the origin $0$. Suppose there exists $R > 0$ such that the open disk $B(0, R)$ is contained in $D$. If $f(0) = 0$ and $\Re(f(z)) \le M$ for all $z$ with $|z| \le R$, then for any $r$ satisfying $0 < r < R$ and for any $z$ with $|z| \le r$, the following inequalities hold:
\begin{enumerate}
    \item $\left| f(z) \right| \le \frac{2Mr}{R-r}$
    \item $\left| f^{(k)}(z) \right| \le \frac{2MR k!}{(R-r)^{k+1}}$ for all integers $k \ge 1$.
\end{enumerate}

\begin{theorem}
The unique relation on the empty set $S = \varnothing$ is the null relation, which is an equivalence relation.
\end{theorem}

\begin{theorem}
In a Hausdorff space $(S, \tau)$, every convergent sequence $(x_n)$ in $S$ has a unique limit.
\end{theorem}

\begin{theorem}
For a set $S$, it is possible to define a binary relation $\circledcirc$ on $S$ such that $\circledcirc$ exhibits any desired combination of reflexivity, symmetry, and transitivity, if and only if $S$ contains at least $3$ elements.
\end{theorem}

\begin{theorem}
The function $f(x) = \dfrac{\sinh x}{x}$ is increasing for all positive real numbers $x$.
\end{theorem}

\begin{theorem}[Euclid's Proposition I.32]
The external angle of a triangle equals the sum of the other two internal angles.
\end{theorem}

\begin{theorem}[Triangle Inequality, Euclid I.20]
Given a triangle $ABC$, the sum of the lengths of any two of its sides is greater than the length of the third side.
\end{theorem}

\begin{theorem}
Let $a_0, a_1, \ldots, a_n$ be the partial denominators of a simple finite continued fraction of length $n \ge 0$. Let $x = [a_0, a_1, \ldots, a_n]$ denote its value. Then the floor of $x$ is given by $\lfloor x \rfloor = a_0$, unless $n = 1$ and $a_1 = 1$, in which case $x = \lfloor x \rfloor = a_0 + 1$.
\end{theorem}

Consider an electric circuit $K$. An electromotive force (EMF) given by $E = E_0 \sin \omega t$ is applied to the circuit starting at time $t=0$. If the initial electric current at $t=0$ is $I_0$, then the electric current $I$ in the circuit at time $t$ is described by the equation:
\begin{equation*}
I = \frac{E_0}{\sqrt{R^2 - L^2 \omega^2}} \sin(\omega t - \alpha) + \left(I_0 - \frac{E_0 L \omega}{R^2 + L^2 \omega^2}\right) e^{-R t / L}
\end{equation*}
where the phase angle $\alpha$ is defined by $\tan \alpha = \frac{L \omega}{R}$.

\begin{theorem}
Let $(M_1, d_1)$ and $(M_2, d_2)$ be metric spaces, and let $f: M_1 \to M_2$ be a continuous mapping. If $S$ is a path-connected subspace of $M_1$, then its image $f(S)$ is a path-connected subspace of $M_2$.
\end{theorem}

\begin{theorem}
Let $(F, +_F, \circ)$ be a field with additive identity $0_F$, and let $(S, +_S, *)$ be a ring with additive identity $0_S$. If $\phi: F \to S$ is a ring homomorphism, then either $\phi$ is a ring monomorphism (that is, $\phi$ is injective) or $\phi$ is the zero homomorphism (that is, $\phi(a) = 0_S$ for all $a \in F$).
\end{theorem}

Prove that, for $x/a > 1$, the indefinite integral of $x^2 \cosh^{-1} \frac x a$ is given by:
\begin{equation*}
\int x^2 \cosh^{-1} \frac x a \ \mathrm d x = \frac {x^3} 3 \cosh^{-1} \frac x a - \frac {(x^2 + 2 a^2) \sqrt {x^2 - a^2} } 9 + C
\end{equation*}

