\end{theorem}

Let $G$ be a group, and let $H$ be a non-empty subset of $G$. Then $H$ is a subgroup of $G$ if and only if the following two conditions hold:
\begin{enumerate}
    \item $H H \subseteq H$
    \item $H^{-1} \subseteq H$
\end{enumerate}
where $H H$ denotes the product of $H$ with itself, and $H^{-1}$ denotes the set of inverses of elements in $H$.

\begin{theorem}
Let $X$ and $Y$ be independent random variables defined on a probability space $(\Omega, \Sigma, \Pr)$. If $\phi_X$ and $\phi_Y$ denote their respective characteristic functions, then the characteristic function of their sum, $\phi_{X+Y}$, is the product of their individual characteristic functions:
\[ \phi_{X+Y} = \phi_X \phi_Y \]
\end{theorem}

\begin{theorem}
For any non-negative real numbers $a$ and $b$ (where $b \neq 0$), and any positive integer $n$, the $n$-th root of a quotient is equal to the quotient of the $n$-th roots, as expressed by the following identity:
\[
    \sqrt [n] {\dfrac a b} = \dfrac {\sqrt [n] a} {\sqrt [n] b}
\]
\end{theorem}

\begin{theorem}
Let $(G, \circ)$ be a monoid with identity element $e$. Then the power set $\mathcal{P}(G)$ of $G$, endowed with the complex product operation $\circ_{\mathcal{P}}$ (induced by $\circ$), forms a monoid $(\mathcal{P}(G), \circ_{\mathcal{P}})$ whose identity element is $\{e\}$.
\end{theorem}

\begin{theorem}
A compact subset of a compact space is not necessarily closed.
\end{theorem}

\begin{theorem}
Matrix similarity is an equivalence relation.
\end{theorem}

\begin{theorem}
Let $n$ be a positive integer. Let $U_n = \{z \in \C : z^n = 1\}$ be the set of complex $n$th roots of unity. Then the sum of the products of the elements of $U_n$ taken $k$ at a time, for $k \in \{2, 3, \dots, n-1\}$, is zero.
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a locally path-connected topological space. If $U \subseteq S$ is an open set, then $U$ (with the subspace topology) is also locally path-connected.
\end{theorem}

\begin{theorem}
Conjunction distributes over disjunction. Specifically, for any propositions $P, Q, R$:
\begin{enumerate}
    \item \textbf{Left Distributivity:} $P \land (Q \lor R) \iff (P \land Q) \lor (P \land R)$
    \item \textbf{Right Distributivity:} $(Q \lor R) \land P \iff (Q \land P) \lor (R \land P)$
\end{enumerate}
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a topological space and $\mathcal B$ a basis for $\tau$. For any subset $A \subseteq S$ and any point $x \in S$, the point $x$ is in the closure of $A$, denoted $A^-$, if and only if for every basis element $U \in \mathcal B$ containing $x$, the intersection $A \cap U$ is non-empty.
\end{theorem}

\begin{theorem}
Let $S_1, S_2, S_3$ be sets. For any relations $\RR_1 \subseteq S_1 \times S_2$ and $\RR_2 \subseteq S_2 \times S_3$, the inverse of their composite relation is equal to the composite of their inverses in reverse order:
\[ \left( \RR_2 \circ \RR_1 \right)^{-1} = \RR_1^{-1} \circ \RR_2^{-1} \]
\end{theorem}

\begin{theorem}
An uncountable discrete topological space is not separable.
\end{theorem}

\begin{theorem}
Let $F = (V, E)$ be a forest with $n$ vertices and $m$ connected components. Then $F$ contains $n - m$ edges.
\end{theorem}

\begin{theorem}
For any ordinal $x$, it is not an element of itself; that is, $x \notin x$.
\end{theorem}

\begin{theorem}
The converse of a conditional statement $p \implies q$ is the contrapositive of its inverse.
\end{theorem}

\begin{theorem}
Let $(S, \preceq_1)$ and $(T, \preceq_2)$ be ordered sets, and let $\phi: S \to T$ be a function. If $\phi$ satisfies the condition that for all $x, y \in S$, $x \preceq_1 y \iff \phi(x) \preceq_2 \phi(y)$, then $\phi$ is an order embedding; that is, $\phi$ is injective and order-preserving (specifically, for all $x, y \in S$, if $x \preceq_1 y$, then $\phi(x) \preceq_2 \phi(y)$).
\end{theorem}

