\begin{theorem}
For real numbers $a$ and $b$, and the imaginary unit $i$, the complex cosecant function $\csc(a+bi)$ is given by the identity:
$$ \csc(a+bi) = \frac{\sin a \cosh b - i \cos a \sinh b}{\sin^2 a \cosh^2 b + \cos^2 a \sinh^2 b} $$
where $\sin$ and $\cos$ denote the real sine and cosine functions, and $\sinh$ and $\cosh$ denote the hyperbolic sine and cosine functions, respectively.
\end{theorem}

\end{document}

\begin{theorem}
Let $A_1$ and $A_2$ be classes, and let $\prec_1$ and $\prec_2$ be relations. If $\phi: (A_1, \prec_1) \to (A_2, \prec_2)$ is an order isomorphism, then $(A_1, \prec_1)$ is a foundational structure if and only if $(A_2, \prec_2)$ is a foundational structure.
\end{theorem}

\begin{theorem}
In a commutative ring with unity, every maximal ideal is a prime ideal.
\end{theorem}

Consider the system of linear first-order ordinary differential equations with constant coefficients:
\begin{align}
\label{eq:1} \frac{dy}{dx} - 3y + 2z &= 0 \\
\label{eq:2} \frac{dz}{dx} + 4y - z &= 0
\end{align}
The general solution to equations \eqref{eq:1} and \eqref{eq:2} is given by:
\begin{align*}
y &= C_1 e^{5x} + C_2 e^{-x} \\
z &= -C_1 e^{5x} + 2 C_2 e^{-x}
\end{align*}

\begin{theorem}
The non-equivalence of two logical propositions, $P$ and $Q$, can be expressed as a disjunction of conjunctions (Disjunctive Normal Form) in the following two equivalent formulations:
\begin{enumerate}
    \item \textbf{Formulation 1:} $P \not\leftrightarrow Q \equiv (P \land \neg Q) \lor (\neg P \land Q)$
    \item \textbf{Formulation 2:} $P \not\leftrightarrow Q \equiv (\neg P \land Q) \lor (P \land \neg Q)$
\end{enumerate}
\end{theorem}

\begin{theorem}
The ordered set of real numbers $(\mathbb{R}, \leqslant)$, with its usual ordering, is not a well-ordered set.
\end{theorem}

\documentclass{article}
\usepackage{amsmath, amssymb}
\begin{document}
\begin{theorem}
Let $(R, +, \circ)$ be a ring. Let $\mathcal{M}_{m \times n}(R)$ denote the set of $m \times n$ matrices with entries from $R$. If for any matrices $\mathbf{A}, \mathbf{B} \in \mathcal{M}_{m \times n}(R)$, their sum $\mathbf{A} + \mathbf{B}$ is defined entrywise, then the operation of matrix addition is associative on $\mathcal{M}_{m \times n}(R)$. That is, for all $\mathbf{A}, \mathbf{B}, \mathbf{C} \in \mathcal{M}_{m \times n}(R)$,
\[ (\mathbf{A} + \mathbf{B}) + \mathbf{C} = \mathbf{A} + (\mathbf{B} + \mathbf{C}) \]
\end{theorem}
\end{document}

Let $G$ be a group and $X$ be a set. There is a one-to-one correspondence between group actions of $G$ on $X$ and permutation representations of $G$ on $X$. More precisely, given a group action $\phi: G \times X \to X$ and a permutation representation $\rho: G \to \operatorname{Sym}(X)$, the following two statements are equivalent:
\begin{enumerate}
    \item $\rho$ is the permutation representation associated with $\phi$.
    \item $\phi$ is the group action associated with $\rho$.
\end{enumerate}

Consider a particle of mass $m$ moving in the plane under the influence of a force $\mathbf F$. Let its position at time $t$ be given in polar coordinates as $(r, \theta)$. If the force $\mathbf F$ is expressed as $\mathbf F = F_r \mathbf u_r + F_\theta \mathbf u_\theta$, where $\mathbf u_r$ and $\mathbf u_\theta$ are the unit vectors in the radial and angular directions, respectively, and $F_r$ and $F_\theta$ are the magnitudes of the components of $\mathbf F$ in these directions, then the second-order ordinary differential equations governing the motion of the particle are:
\begin{align*}
F_\theta &= m \left(r \dfrac{\mathrm{d}^2 \theta}{\mathrm{d} t^2} + 2 \dfrac{\mathrm{d} r}{\mathrm{d} t} \dfrac{\mathrm{d} \theta}{\mathrm{d} t} \right) \\
F_r &= m \left(\dfrac{\mathrm{d}^2 r}{\mathrm{d} t^2} - r \left(\dfrac{\mathrm{d} \theta}{\mathrm{d} t} \right)^2\right)
\end{align*}

\begin{theorem}
Let $G$ and $H$ be solvable groups. Then their direct product $G \times H$ is also solvable.
\end{theorem}

\begin{theorem}
Let $\mathbf A$ be a propositional formula. Then $\mathbf A$ is satisfiable if and only if its negation $\neg \mathbf A$ is falsifiable.
\end{theorem}

\begin{theorem}
Let $(X, d)$ be a metric space and $S \subseteq X$. Let $\tau_d$ be the topology induced by the metric $d$. Then $S$ is dense in $(X, \tau_d)$ if and only if every open ball in $(X, d)$ contains an element of $S$.
\end{theorem}

Let $H$ be a Hilbert space and $A \in B(H)$ be a bounded linear operator. The following conditions are equivalent:
\begin{enumerate}
    \item $AA^* = A^*A$, i.e., $A$ is a normal operator.
    \item For all $h \in H$, $\|Ah\|_H = \|A^*h\|_H$.
\end{enumerate}
Furthermore, if $H$ is a complex Hilbert space, these conditions are also equivalent to:
\begin{itemize}
    \item[(3)] $\operatorname{Re}(A) \operatorname{Im}(A) = \operatorname{Im}(A) \operatorname{Re}(A)$, i.e., the real and imaginary parts of $A$ commute.
\end{itemize}

\documentclass{article}
\usepackage{amsmath, amssymb}
\begin{document}
\noindent
\textbf{Theorem:} The set of rational numbers is dense. That is, for any two distinct rational numbers $a$ and $b$ such that $a < b$, there exists a rational number $c$ such that $a < c < b$.
\end{document}

\begin{theorem}
A subset of a linearly independent set is also linearly independent.
\end{theorem}

\begin{theorem}
Let $p$ be a prime number. If $G$ is a group of order $2p$, then $G$ is isomorphic to either the cyclic group $C_{2p}$ or the dihedral group $D_p$.
\end{theorem}

\begin{theorem}
If $n$ is an even positive integer, then a rhodonea curve $R$ defined by either of the polar equations
$$ r = a \cos n \theta \quad \text{or} \quad r = a \sin n \theta $$
has $2n$ petals.
\end{theorem}

\begin{theorem}
Let $S$ and $T$ be sets. Let $S \setminus T$ denote the set difference of $S$ and $T$, and $S \cap T$ denote their set intersection. If $S \setminus T \ne \emptyset$ and $S \cap T \ne \emptyset$, then the collection $\{S \setminus T, S \cap T\}$ forms a partition of $S$.
\end{theorem}

\begin{theorem}
Let $m, n, k \in \mathbb{N}$ be natural numbers such that $k \neq 0$. Then $m < n \iff m \cdot k < n \cdot k$.
\end{theorem}

\begin{theorem}
Let $d_1$ and $d_2$ be two metrics on a set $A$. If $d_1$ and $d_2$ are Lipschitz equivalent, then they are topologically equivalent.
\end{theorem}

\begin{theorem}
The differential of $x^2 + y^2$ is given by the expression $\mathrm{d}(x^2 + y^2) = 2(x \, \mathrm{d}x + y \, \mathrm{d}y)$.
\end{theorem}

\begin{theorem}
If $\sum_{n=1}^\infty a_n$ is an absolutely convergent series of real numbers, then $\sum_{n=1}^\infty a_n$ is convergent.
\end{theorem}

For any positive integer $n$, the following identity holds:
\begin{align*}
x^{2 n + 1} - y^{2 n + 1} &= \left(x - y\right) \prod_{k = 1}^n \left( x^2 - 2 x y \cos \frac{2 \pi k}{2 n + 1} + y^2 \right) \\
&= \left(x - y\right) \left( x^2 - 2 x y \cos \frac{2 \pi}{2 n + 1} + y^2 \right) \left( x^2 - 2 x y \cos \frac{4 \pi}{2 n + 1} + y^2 \right) \dotsm \left( x^2 - 2 x y \cos \frac{2 n \pi}{2 n + 1} + y^2 \right)
\end{align*}

\begin{theorem}
Let $T_1$ and $T_2$ be topological spaces, and let $T = T_1 \times T_2$ be their product space. Let $T'$ be a topological space. Consider two maps $f \colon T' \to T_1$ and $g \colon T' \to T_2$. Define the product map $f \times g \colon T' \to T$ such that for all $x \in T'$, $(f \times g)(x) = (f(x), g(x))$. Then, the map $f \times g$ is continuous if and only if $f$ and $g$ are both continuous.
\end{theorem}

\noindent Prove the following indefinite integral identity:
\begin{equation*}
\int \frac {\tanh^{-1} \left(\frac x a\right)} {x^2} \,dx = \frac {-\tanh^{-1} \left(\frac x a\right)} x + \frac 1 {2 a} \ln \left(\frac {x^2} {a^2 - x^2}\right) + C
\end{equation*}

\begin{theorem}
Let $\mathcal R$ be a relation on a set $S$. If $\mathcal R$ is asymmetric, then its inverse $\mathcal R^{-1}$ is also asymmetric.
\end{theorem}

\begin{theorem}
Let $\SS$ be an algebra of sets and $f: \SS \to \overline \R$ be an additive function on $\SS$. Then $f$ is also strongly additive, which means that for all $A, B \in \SS$, the following equality holds:
\[
\map f {A \cup B} + \map f {A \cap B} = \map f A + \map f B
\]
\end{theorem}

\begin{theorem}
Let $R$ be a finite set of indices, and let $(a_j)_{j \in R}$ be a collection of terms. If $\pi: R \to R$ is a permutation of the indices, then
\[
\sum_{j \in R} a_j = \sum_{j \in R} a_{\pi(j)}.
\]
\end{theorem}

\begin{theorem}
Let $(x_n)$ and $(y_n)$ be sequences of real numbers such that $\lim_{n \to \infty} x_n = l$ and $\lim_{n \to \infty} y_n = m$. If $x_n \le y_n$ for all sufficiently large $n$, then $l \le m$.
\end{theorem}

\begin{theorem}
For any integers $a$ and $b$ with $b > 0$, there exist unique integers $q$ and $r$ such that $a = qb + r$ and $0 \le r < b$, i.e., $\forall a, b \in \mathbb{Z}, b > 0: \exists! q, r \in \mathbb{Z}: a = q b + r, 0 \le r < b$.
\end{theorem}

\begin{theorem}
Let $\mathbb{R} \setminus \mathbb{Q}$ be the set of irrational numbers, equipped with the Euclidean topology $\tau_d$. If $S \subseteq \mathbb{R} \setminus \mathbb{Q}$ is a compact set, then $S$ is nowhere dense in $\mathbb{R} \setminus \mathbb{Q}$.
\end{theorem}

Consider a body $B$ with instantaneous mass $m$ at time $t$, moving with velocity $\mathbf v$ and subject to an external force $\mathbf F$. If mass is added to the body at a rate of $\frac{\mathrm{d}m}{\mathrm{d}t}$, and this added mass possesses a velocity of $\mathbf v + \mathbf w$ (where $\mathbf w$ is the relative velocity of the added mass with respect to the body $B$), then the equation of motion for body $B$ is given by:
\begin{equation*}
\mathbf w \frac{\mathrm{d}m}{\mathrm{d}t} + \mathbf F = m \frac{\mathrm{d}\mathbf v}{\mathrm{d}t}
\end{equation*}

\begin{theorem}
Let $S$ be a finite set. If $f: S \to S$ is an injective function, then $f$ is also a surjective function.
\end{theorem}

The hyperbolic cosine function has the power series expansion:
\begin{equation*}
    \cosh x = \sum_{n=0}^\infty \frac{x^{2n}}{(2n)!} = 1 + \frac{x^2}{2!} + \frac{x^4}{4!} + \frac{x^6}{6!} + \cdots
\end{equation*}
valid for all $x \in \R$.

\begin{theorem}
Let $\sequence{x_n}$ be a real sequence. If $\sequence{x_n}$ converges to a non-zero limit $a$, then eventually every term of $\sequence{x_n}$ becomes non-zero. That is, there exists a natural number $N \in \mathbb{N}$ such that for all $n \ge N$, $x_n \ne 0$.
\end{theorem}

\begin{theorem}
The set $\mathbf P$ of all URM programs is countably infinite.
\end{theorem}

\begin{theorem}
Let $(G_1, \circ_1)$ and $(G_2, \circ_2)$ be groups with identity elements $e_1$ and $e_2$ respectively.
Then their external direct product, $(G_1 \times G_2, \circ)$, is a group whose identity element is $(e_1, e_2)$.
\end{theorem}

\begin{theorem}
If a sequence of real numbers $\sequence {x_n}$ is decreasing and bounded below, then it converges to its infimum.
\end{theorem}

\begin{theorem}
Let $M = (A, d)$ be a metric space. Then $M$ is a Hausdorff space.
\end{theorem}

\begin{theorem}
A finite set cannot be put into bijection with any of its proper subsets. In other words, a finite set is not Dedekind-infinite.
\end{theorem}

\begin{theorem}
Set union is distributive over set intersection. That is, for any sets $R, S, T$, the following equality holds:
\[
R \cup (S \cap T) = (R \cup S) \cap (R \cup T)
\]
\end{theorem}

\begin{theorem}
For any complex number $z$, with $\overline{z}$ denoting its complex conjugate, it holds that $\overline{z^2} = (\overline{z})^2$.
\end{theorem}

\begin{theorem}
For every natural number $n$, the sum of the sequence of integers that increases from $1$ to $n$ and then decreases back to $1$ is equal to $n^2$. That is,
$$ 1 + 2 + \dots + n + (n-1) + \dots + 1 = n^2. $$
\end{theorem}

\begin{theorem}
For any set $S$, there exists a total ordering on $S$.
\end{theorem}

Let $S$ be a bounded above subset of the real numbers $\R$. Then:
\begin{enumerate}
    \item The set $\{-x : x \in S\}$ is bounded below.
    \item $-\sup_{x \in S} x = \inf_{x \in S} (-x)$.
\end{enumerate}
where $\sup$ and $\inf$ denote the supremum and infimum, respectively.

Theorem: Let $\mathbb T$ be a collection of subsets of a universal set $\mathbb U$. Then the following De Morgan's Laws hold:
\begin{align*}
    \left( \bigcap_{A \in \mathbb T} A \right)^c &= \bigcup_{A \in \mathbb T} A^c \quad \text{(Complement of Intersection)} \\
    \left( \bigcup_{A \in \mathbb T} A \right)^c &= \bigcap_{A \in \mathbb T} A^c \quad \text{(Complement of Union)}
\end{align*}

\begin{theorem}
If $T = (S, \tau)$ is a fully normal space, then $T$ is a normal space.
\end{theorem}

\text{Let } $z_1$ \text{and } $z_2$ \text{be complex numbers, considered as vectors. The modulus of the projection of } $z_1$ \text{onto } $z_2$, \text{denoted by } $\pr_1(z_1, z_2)$, \text{is given by the formula:}
$$ \cmod {\pr_1(z_1, z_2)} = \frac {\cmod {z_1 \circ z_2}} {\cmod {z_2}} $$
\text{where } $z_1 \circ z_2$ \text{represents the complex dot product of } $z_1$ \text{and } $z_2$, \text{and } $\cmod {z_2}$ \text{denotes the complex modulus of } $z_2$.

\begin{theorem}[Euclid's Elements, Book III, Proposition 4]
If in a circle two chords that are not diameters intersect each other, then they do not bisect each other.
\end{theorem}

\begin{theorem}
Let $(S, \circ, \preceq)$ be an ordered group with identity element $e$. For any elements $x, y, z \in S$, the following statements are equivalent:
\begin{enumerate}
    \item $x \prec y$
    \item $x \circ z \prec y \circ z$
    \item $z \circ x \prec z \circ y$
    \item $y^{-1} \prec x^{-1}$
    \item $e \prec y \circ x^{-1}$
    \item $e \prec x^{-1} \circ y$
\end{enumerate}
\end{theorem}

\begin{theorem}
The set of rational numbers $\Q$ is not closed in the real number line $\R$ when $\R$ is equipped with the usual (Euclidean) topology.
\end{theorem}

\begin{theorem}
Let $(a_n)$ be a sequence of real numbers. If $\lim_{n \to \infty} a_n \ne 0$, then the series $\sum_{n=1}^\infty a_n$ diverges.
\end{theorem}

Let $(\mathbb{C}_{
e 0}, 	imes)$ denote the multiplicative group of non-zero complex numbers, and let $(\mathbb{R}_{
e 0}, 	imes)$ denote the multiplicative group of non-zero real numbers. Then the direct product $(\mathbb{R}_{
e 0}, 	imes) 	imes (\mathbb{R}_{
e 0}, 	imes)$ is not isomorphic to $(\mathbb{C}_{
e 0}, 	imes)$.

\begin{theorem}
Every compact one-dimensional manifold is diffeomorphic to either a circle or a closed interval.
\end{theorem}

\begin{theorem}
Let $n \in \mathbb{N}$ with $n \ge 3$. The center of the dihedral group $D_n$, of order $2n$, defined by the presentation $D_n = \langle \alpha, \beta \mid \alpha^n = \beta^2 = e, \beta \alpha \beta = \alpha^{-1} \rangle$, is given by
\[
Z(D_n) = \begin{cases}
\{e\} & \text{if } n \text{ is odd} \\
\{e, \alpha^{n/2}\} & \text{if } n \text{ is even}
\end{cases}
\]
\end{theorem}

\begin{theorem}
A metric space is compact if and only if it is countably compact.
\end{theorem}

\begin{problem}
Let $X$ be a set, and let $\mathcal A$ be a non-empty collection of subsets of $X$. Let $x \mapsto x'$ be a unary operation on $X$. Assume that $\mathcal A$ has finite character. If for every $A \in \mathcal A$ and every $x \in X$, either $A \cup \{x\} \in \mathcal A$ or $A \cup \{x'\} \in \mathcal A$, then prove that there exists a $B \in \mathcal A$ such that for every $x \in X$, either $x \in B$ or $x' \in B$.
\end{problem}

\begin{theorem}
Let $[a, b]$ be a closed real interval such that $a < b$. If $f: [a, b] \to \mathbb{R}$ is a monotone real function, then $f$ is Darboux integrable over $[a, b]$.
\end{theorem}

