\newtheorem*{theorem}{Theorem}
\begin{theorem}
Let $D \subseteq \mathbb{C}$ be a connected domain and let $f: D \to \mathbb{C}$ be a complex-differentiable function. If $f'(z) = 0$ for all $z \in D$, then $f$ is constant on $D$.
\end{theorem}

\end{document}

\begin{theorem}
The sum of an even integer and $5$ is an odd integer.
\end{theorem}

\begin{theorem}
Let $S$ be a set. Let $(T, +, \circ)$ be an algebraic structure with two binary operations $+$ and $\circ$. Let $T^S$ be the set of all mappings from $S$ to $T$. Let $(T^S, +', \circ')$ be the structure induced on $T^S$ by $+$ and $\circ$. If $\circ$ is distributive over $+$ in $T$, then $\circ'$ is distributive over $+'$ in $T^S$.
\end{theorem}

\begin{proposition}[Euclid I.25]
If two triangles have two sides of one equal in length to two sides of the other, respectively, then the triangle with the longer third side also has the greater angle included between those two sides.
\end{proposition}

\begin{theorem}
Let $T = (S, \tau)$ be a topological space. If $T$ is Lindelöf and countably paracompact, then $T$ is paracompact.
\end{theorem}

\begin{theorem}
Let $a, m, n$ be real numbers such that $m \neq n$. Then the following reduction formula holds for the indefinite integral of powers of sine and cosine:
\[
\int \frac {\sin^m (ax)} {\cos^n (ax)} \mathrm{d}x = \frac {-\sin^{m - 1} (ax)} {a (m - n) \cos^{n - 1} (ax)} + \frac {m - 1} {m - n} \int \frac {\sin^{m - 2} (ax)} {\cos^n (ax)} \mathrm{d}x + C
\]
\end{theorem}

\begin{theorem}
There exists a topological space which is a $T_2$ (Hausdorff) space, but is not a completely Hausdorff space.
\end{theorem}

\begin{theorem}
Every well-formed formula (WFF) of predicate logic which begins with a left parenthesis or a negation symbol has exactly one principal connective.
\end{theorem}

Prove that for $0 < p \le 2m$, the following integral identity holds:
\begin{align*}
\int \frac{x^{p - 1} \, \mathrm{d}x}{x^{2 m} + a^{2 m}} &= \frac{1}{m a^{2 m - p}} \sum_{k = 1}^m \sin \left( \frac{(2 k - 1) p \pi}{2 m} \right) \arctan \left( \frac{x + a \cos \left( \frac{(2 k - 1) \pi}{2 m} \right)}{a \sin \left( \frac{(2 k - 1) \pi}{2 m} \right)} \right) \\
&\quad - \frac{1}{2 m a^{2 m - p}} \sum_{k = 1}^m \cos \left( \frac{(2 k - 1) p \pi}{2 m} \right) \ln \left( x^2 + 2 a x \cos \left( \frac{(2 k - 1) \pi}{2 m} \right) + a^2 \right)
\end{align*}

\begin{theorem}
Let $\omega$ denote the set of natural numbers as defined by the von Neumann construction on a Zermelo universe $V$. Then $\bigcup \omega = \omega$.
\end{theorem}

\begin{theorem}
A square can be dissected into $8$ acute triangles.
\end{theorem}

Prove that for any positive integer $n$, the following identity holds:
\begin{equation*}
\left(\sum_{k=1}^{n-1} 5 \times 10^{2k-1} + 6\right)^2 - \left(\sum_{k=1}^{n-1} 5 \times 10^{2k-1} - 5\right)^2 = \sum_{k=0}^{2n-1} 10^k
\end{equation*}
Here, the terms in the difference of squares represent numbers of the form $\underbrace{5050 \ldots 56}_{n - 1 \text{ fives}}$ and $\underbrace{5050 \ldots 45}_{n - 1 \text{ fives}}$ respectively, and the identity generalizes the pattern observed in examples such as $6^2 - 5^2 = 11$, $56^2 - 45^2 = 1111$, and $5056^2 - 5045^2 = 111111$.

\begin{theorem}
Let $(G, \circ)$ be an infinite group. Then $(G, \circ)$ has infinitely many distinct subgroups.
\end{theorem}

\begin{theorem}
For a Balanced Incomplete Block Design (BIBD) with parameters $v, b, r, k, \lambda$, where $v, b, r, k, \lambda$ are integers, the following properties hold:
\begin{enumerate}
    \item $bk = rv$
    \item $\lambda (v - 1) = r (k - 1)$
    \item $b \binom{k}{2} = \lambda \binom{v}{2}$
    \item $k < v$
    \item $r > \lambda$
\end{enumerate}
\end{theorem}

\begin{theorem}
Let $G$ and $H$ be unitary $R$-modules. Given an ordered basis $(a_1, \dots, a_n)$ for $G$ and a sequence of elements $(b_1, \dots, b_n)$ in $H$, there exists a unique linear transformation $\phi: G \to H$ such that $\phi(a_k) = b_k$ for all $k \in \{1, \dots, n\}$.
\end{theorem}

\begin{theorem}
Let $(S, \preceq)$ be a well-ordered set. If we define $A = \left\{a^\prec \mid a \in S\right\}$, where $a^\prec = \left\{x \in S \mid x \prec a\right\}$ is the strict lower closure of $a$ in $S$, then $(S, \preceq)$ is order isomorphic to $(A, \subseteq)$, where $\subseteq$ denotes the subset relation on $A$.
\end{theorem}

\begin{theorem}
Let $\mathcal C$ be a smooth curve parameterized by the vector function $\mathbf r(t)$ for $a \le t \le b$. If $f$ is a differentiable function of two or three variables such that its gradient vector $\nabla f$ is continuous on $\mathcal C$, then
\[
    \int_\mathcal C \nabla f \cdot d \mathbf r = f \left({\mathbf r \left({b}\right)}\right) - f \left({\mathbf r \left({a}\right)}\right)
\]
\end{theorem}

\begin{theorem}
Let $z_1$, $z_2$, and $z_3$ be complex numbers. These numbers represent the vertices of an equilateral triangle on the complex plane if and only if
\[
z_1^2 + z_2^2 + z_3^2 = z_1 z_2 + z_2 z_3 + z_3 z_1.
\]
\end{theorem}

\begin{theorem}
For any positive real numbers $a$ and $b$, and any real number $x$, the following property holds:
\[ (ab)^x = a^x b^x \]
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a topological space, and let $x \in \tau$ be an open set.
Then $x$ is a compact element in the ordered set of open sets $(\tau, \subseteq)$ (meaning that for any collection of open sets $\mathcal{A} \subseteq \tau$, if $x \subseteq \bigcup_{A \in \mathcal{A}} A$, then there exists a finite subcollection $\mathcal{A}' \subseteq \mathcal{A}$ such that $x \subseteq \bigcup_{A' \in \mathcal{A}'} A'$) if and only if the topological subspace $(x, \tau_x)$ is compact.
\end{theorem}

\begin{theorem}
If $u$ is a differentiable function of $x$, then the derivative of the natural logarithm of $u$ with respect to $x$ is given by
\[
\frac{\mathrm{d}}{\mathrm{d}x} (\ln u) = \frac{1}{u} \frac{\mathrm{d}u}{\mathrm{d}x}.
\]
\end{theorem}

