\noindent
Theorem: For any positive integer $n$, the $2n$-th Bernoulli number $B_{2n}$ can be expressed in terms of Euler numbers as follows:
\begin{equation*}
B_{2 n} = \frac {2n} {2^{2 n} \left(2^{2 n} - 1\right)} \sum_{k = 1}^n \binom {2 n - 1} {2 k - 1} E_{2 n - 2 k}
\end{equation*}
where $B_m$ denotes the $m$-th Bernoulli number and $E_m$ denotes the $m$-th Euler number.

\end{document}

\begin{theorem}
Let $n$ be a positive integer with $d_1$ digits, and let $m$ be a repdigit number with $d_2$ digits such that $d_2 > d_1$. If $r$ is the sum of the quotient and remainder when the product $mn$ is divided by $10^{d_2}$, then $r$ is a repdigit number.
\end{theorem}

\begin{lemma}[Euclid's Elements, Book XII, Proposition 2, Lemma]
If two circles are unequal, then it is possible to inscribe a regular polygon in the larger circle such that the sum of the areas of the segments of the circle remaining outside the polygon is less than any given positive area.
\end{lemma}

\begin{proposition}
It is possible to arrange six congruent circles around a central circle that is also congruent to each of the six surrounding circles.
\end{proposition}

\begin{theorem}
Let $\struct {S, \circ_1}$ and $\struct {T, \circ_2}$ be monoids with identity elements $e_S$ and $e_T$, respectively. If $\struct {S \times T, \circ}$ is the external direct product of $\struct {S, \circ_1}$ and $\struct {T, \circ_2}$, then $\tuple {e_S, e_T}$ is the identity element of $\struct {S \times T, \circ}$.
\end{theorem}

\begin{theorem}
Let $(S, \circ, \preceq)$ be an ordered monoid with identity element $e$. If $x, y \in S$ are invertible elements, then $x \prec y \iff y^{-1} \prec x^{-1}$.
\end{theorem}

\begin{theorem}
Let $I$ be a real interval and let $f: I \to \mathbb{R}$ be a continuous function. Then the image of $f$ is a real interval.
\end{theorem}

\begin{theorem}
In the topological space of rational numbers $(\Q, \tau_d)$, where $\tau_d$ denotes the usual Euclidean topology, every singleton subset of $\Q$ is nowhere dense.
\end{theorem}

\begin{theorem}
In the Euclidean space $\R$, the real number line, every closed interval $[a, b]$ is a closed set.
\end{theorem}

\begin{theorem}
If $z_1 = r_1 e^{i \theta_1}$ and $z_2 = r_2 e^{i \theta_2}$ are complex numbers expressed in exponential form, then their quotient is given by the formula:
$$ \frac{z_1}{z_2} = \frac{r_1}{r_2} e^{i (\theta_1 - \theta_2)}. $$
\end{theorem}

\begin{theorem}
Let $n \in \mathbb{Q}$. Consider the function $f: \mathbb{R} \to \mathbb{R}$ defined by $f(x) = x^n$. Then its derivative is given by $f'(x) = n x^{n-1}$ for all $x$ for which $x^n$ is defined. It is important to note that when $x=0$ and $n=0$, the derivative $f'(x)$ is undefined.
\end{theorem}

\begin{theorem}
For any positive real number $x$ and any positive integer $n$, the following inequality holds:
\[ \sqrt[n]{1+x} \le 1 + \frac{x}{n} \]
\end{theorem}

Let $R$ be a ring, and let $J$ and $K$ be ideals of $R$ such that $J \subseteq K$. Then:
\begin{enumerate}
    \item The set $K/J$, defined as the quotient ring of $K$ by $J$, is an ideal of the quotient ring $R/J$.
    \item The quotient ring $\dfrac{R/J}{K/J}$ is ring isomorphic to the quotient ring $\dfrac{R}{K}$.
\end{enumerate}

\begin{theorem}
Let $(S, \circ)$ be an algebraic structure. If $e_L \in S$ is a left identity and $e_R \in S$ is a right identity for $\circ$, then $e_L = e_R$. This element, which is an identity element for $\circ$, is unique; it is the only left identity and the only right identity in $S$.
\end{theorem}

\begin{theorem}
Let $R$ be a ring with unity. If $\{M_i\}_{i \in I}$ is a family of unitary $R$-modules, then their direct product, denoted by $M = \prod_{i \in I} M_i$, is a unitary $R$-module.
\end{theorem}

\begin{theorem}
For a finite set $S$, any surjective function $f \colon S \to S$ is a permutation.
\end{theorem}

\begin{theorem}
If the Well-Ordering Theorem holds, then the Hausdorff Maximal Principle holds.
\end{theorem}

\begin{theorem}
If two straight lines are cut by a transversal and are parallel, then their corresponding angles are equal.
\end{theorem}

\begin{theorem}
Let $(S, \preceq)$ be an ordered set and $T \subseteq S$. Let $T^*$ be the set of all upper bounds of $T$ in $S$. Then $T^* \cap T \ne \varnothing$ if and only if $T$ has a greatest element $M$, and $T^* \cap T = \{M\}$.
\end{theorem}

\theoremstyle{plain}
\newtheorem*{theorem}{Theorem}
\begin{theorem}
Let $\CC$ denote the Cartesian plane. If $P = (x, y)$ is a point in $\CC$ such that both $x$ and $y$ are rational numbers, then $P$ is constructible using a compass and straightedge.
\end{theorem}

\begin{proposition}
For any two sets $A$ and $B$, the characteristic function of their union $A \cup B$ is equal to the maximum of their individual characteristic functions:
\[
\chi_{A \cup B} = \max \left\{ \chi_A, \chi_B \right\}.
\]
\end{proposition}

Let $H$ and $K$ be Hilbert spaces, and let $A: H \to K$ be a bounded linear transformation. Then the following statements defining the norm of $A$ are equivalent:
\begin{enumerate}
    \item $\left\|A\right\| = \sup \left\{ \left\|Ah\right\|_K : \left\|h\right\|_H \le 1 \right\}$
    \item $\left\|A\right\| = \sup \left\{ \frac{\left\|Ah\right\|_K}{\left\|h\right\|_H} : h \in H, h \ne \mathbf{0}_H \right\}$
    \item $\left\|A\right\| = \inf \left\{ c > 0 : \forall h \in H : \left\|Ah\right\|_K \le c \left\|h\right\|_H \right\}$
\end{enumerate}

\begin{theorem}
A lattice $(S, \vee, \wedge, \preceq)$ has at most one bottom element.
\end{theorem}

\begin{theorem}
The topological space $(\mathbb{Q}, \tau_d)$, where $\mathbb{Q}$ is the set of rational numbers and $\tau_d$ denotes the Euclidean (subspace) topology inherited from the real numbers $\mathbb{R}$, is not a scattered space.
\end{theorem}

\begin{theorem}
For any two sets $S$ and $T$, $S = T$ if and only if their symmetric difference $S \Delta T$ is the empty set $\emptyset$, which can be formally stated as $S = T \iff S \Delta T = \emptyset$.
\end{theorem}

\begin{theorem}
Let $S$ be a finite set with cardinality $n$. For any integer $m$ such that $0 \le m \le n$, there exists a subset $X \subseteq S$ such that its cardinality is $m$.
\end{theorem}

The second-order ordinary differential equation
\begin{equation*}
(x^2 + x) y'' + (2 - x^2) y' - (2 + x) y = x (x + 1)^2
\end{equation*}
has the general solution
\begin{equation*}
y = C_1 e^x + \frac{C_2}{x} - x - 1 - \frac{x^2}{3}.
\end{equation*}

Let $\mathbb{N}_{>0}$ denote the set of positive integers, $<$ the standard strict ordering on $\mathbb{N}_{>0}$, and $+$ the standard addition operation on $\mathbb{N}_{>0}$. Addition is cancellable with respect to the strict ordering on $\mathbb{N}_{>0}$, meaning that for all $a, b, c \in \mathbb{N}_{>0}$, the following implications hold:
\begin{enumerate}
    \item $a + c < b + c \implies a < b$
    \item $a + b < a + c \implies b < c$
\end{enumerate}

A fraction $\frac{A}{B}$ is called a pandigital fraction if the digits used in $A$ and $B$ together form a permutation of the digits $1, 2, \dots, 9$. It is known that the fraction $\frac{1}{2}$ can be expressed in exactly seven such ways. These expressions are:
\begin{align*}
\frac{1}{2} &= \frac{2394}{16\,758} \\
\frac{1}{2} &= \frac{2637}{18\,459} \\
\frac{1}{2} &= \frac{4527}{31\,689} \\
\frac{1}{2} &= \frac{5274}{36\,918} \\
\frac{1}{2} &= \frac{5418}{37\,926} \\
\frac{1}{2} &= \frac{5976}{41\,832} \\
\frac{1}{2} &= \frac{7614}{53\,298}
\end{align*}

\begin{theorem}
The dot product is an inner product.
\end{theorem}

Prove that for all positive integers $n \in \mathbb{Z}_{>0}$, the following identity holds:
\[
\frac{1}{n} = \sum_{k=0}^{n-1} (-1)^k \dbinom{n-1}{k} \frac{1}{k+1}
\]
where $\dbinom{n-1}{k}$ denotes a binomial coefficient.
For example, for $n=1, 2, 3, 4, 5$, we have:
\begin{align*}
\frac{1}{1} &= 1 \\
\frac{1}{2} &= 1 - \frac{1}{2} \\
\frac{1}{3} &= 1 - 2 \times \frac{1}{2} + \frac{1}{3} \\
\frac{1}{4} &= 1 - 3 \times \frac{1}{2} + 3 \times \frac{1}{3} - \frac{1}{4} \\
\frac{1}{5} &= 1 - 4 \times \frac{1}{2} + 6 \times \frac{1}{3} - 4 \times \frac{1}{4} + \frac{1}{5}
\end{align*}

Let $x(z)$ be the continuous real-valued function satisfying the implicit equation $\ln x(z) = z (x(z))^t$ with the initial condition $x(0) = 1$. Then, for any real numbers $r$ and $t$, the $r$-th power of $x(z)$ can be expressed as the series:
\begin{equation*}
x^r = \sum_{k \ge 0} \frac{r (r + k t)^{k - 1}}{k!} z^k = 1 + r z + \frac{r (r + 2 t)}{2} z^2 + \cdots
\end{equation*}

\begin{theorem}
An integer $N$ is divisible by $2$ if and only if its units digit is divisible by $2$.
More precisely, if $N$ is expressed in decimal notation as $N = (a_n a_{n-1} \ldots a_1 a_0)_{10}$, which can be written in expanded form as $N = a_0 + 10a_1 + 10^2 a_2 + \cdots + 10^n a_n$, then $N \equiv 0 \pmod{2}$ if and only if $a_0 \equiv 0 \pmod{2}$.
\end{theorem}

Let $\mathbf C$ be a metacategory. Given the following commutative diagram in $\mathbf C$:
\begin{center}
  \begin{xymatrix}@+1em@L+2px{
   A'
    \ar[rr]^*{h_\alpha}
    \ar[dd]_*{\alpha'}
  & &
   A
    \ar[rd]^*{\gamma}
    \ar[dd]^(.4)*{\alpha}
  \\ &
   B'
    \ar[ld]^*{\beta'}
    \ar[rr] |{\hole} ^(.3)*{h_\beta}
  & &
   B
    \ar[ld]^*{\beta}
  \\
   C'
    \ar[rr]_*{h}
  & &
   C
  }\end{xymatrix}
\end{center}
If the two squares within this diagram are pullback diagrams, then there exists a unique morphism $\gamma': A' \to B'$ such that the extended diagram
\begin{center}
  \begin{xymatrix}@+1em@L+2px{
   A'
    \ar[rr]^*{h_\alpha}
    \ar[dd]_*{\alpha'}
    \ar@{-->}[rd]^*{\gamma'}
  & &
   A
    \ar[rd]^*{\gamma}
    \ar[dd]^(.4)*{\alpha}
  \\ &
   B'
    \ar[ld]^*{\beta'}
    \ar[rr] |{\hole} ^(.3)*{h_\beta}
  & &
   B
    \ar[ld]^*{\beta}
  \\
   C'
    \ar[rr]_*{h}
  & &
   C
  }\end{xymatrix}
\end{center}
commutes. Furthermore, this unique morphism $\gamma'$ ensures that the following square is a pullback:
\begin{center}
  \begin{xymatrix}@+.5em@L+2px{
   A'
    \ar[r]^*{h_\alpha}
    \ar[d]_*{\gamma'}
  &
   A
    \ar[d]^*{\gamma}
  \\
   B'
    \ar[r]_*{h_\beta}
  &
   B
  }\end{xymatrix}
\end{center}

\begin{theorem}
Let $(\mathbb K, \norm{\,\cdot\,})$ be a valued field. If the infinite product $\displaystyle \prod_{n=1}^\infty a_n$ converges to $a$, and the infinite product $\displaystyle \prod_{n=1}^\infty b_n$ converges to $b$, then the infinite product $\displaystyle \prod_{n=1}^\infty a_nb_n$ converges to $ab$.
\end{theorem}

\begin{theorem}
Let $\On$ denote the class of all ordinals. The subset relation, $\subseteq$, when restricted to $\On$, constitutes a strong well-ordering. Specifically, this means that $\subseteq$ is an ordering on $\On$, and for any non-empty subclass $A$ of $\On$, there exists a $\subseteq$-smallest element in $A$.
\end{theorem}

\begin{theorem}
Let $S$ be a non-empty subset of $\R$. If for some $\xi \in \R$, the distance $d(\xi, S) = 0$, then there exists a sequence $(x_n)$ in $S$ such that $\lim_{n \to \infty} x_n = \xi$.
\end{theorem}

\begin{theorem}
Let $(X, \Sigma)$ be a measurable space and $\mathcal{G} \subseteq \mathcal{P}(X)$ be a generator for $\Sigma$, meaning $\Sigma = \sigma(\mathcal{G})$.
Suppose that $\mathcal{G}$ satisfies the following conditions:
\begin{enumerate}
    \item $\mathcal{G}$ is closed under finite intersections; that is, for all $G, H \in \mathcal{G}$, $G \cap H \in \mathcal{G}$.
    \item There exists an exhausting sequence $(G_n)_{n \in \mathbb{N}}$ in $\mathcal{G}$ such that $G_n \uparrow X$.
\end{enumerate}
Let $\mu$ and $\nu$ be measures on $(X, \Sigma)$. If, in addition, the following conditions hold:
\begin{enumerate}[resume]
    \item $\mu(G) = \nu(G)$ for all $G \in \mathcal{G}$.
    \item $\mu(G_n)$ is finite for all $n \in \mathbb{N}$.
\end{enumerate}
Then $\mu = \nu$.

\par
\noindent (Note: By the theorem on countable covers inducing exhausting sequences, condition (2) may be replaced by the existence of a countable $\mathcal{G}$-cover $(G_n)_{n \in \mathbb{N}}$ for $X$, still subject to condition (4).)
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a finite complement space. If $H$ is an infinite subset of $S$, then every point of $S$ is a limit point of $H$.
\end{theorem}

\begin{theorem}[Euclid V.7]
If $a=b$, then $a:c = b:c$ and $c:a = c:b$.
\end{theorem}

\begin{theorem}
Let $T = (S, \tau)$ be an ultraconnected topological space. Then $T$ is not necessarily arc-connected.
\end{theorem}

The linear first-order ordinary differential equation
\begin{equation*}
y' + y = 2 x e^{-x} + x^2
\end{equation*}
has the general solution
\begin{equation*}
y = x^2 e^{-x} + x^2 - 2 x + 2 + C e^{-x}.
\end{equation*}

\begin{theorem}
In a particular point space $T = \left(S, \tau_p\right)$, every subset $H \subseteq S$ is either open or closed in $T$, and the only subsets of $S$ that are simultaneously open and closed in $T$ are $S$ and $\varnothing$.
\end{theorem}

Prove that for all $n \in \mathbb{Z}_{\ge 0}$, the following identity holds:
\begin{equation*}
2^{n - 1} F_n = \sum_k 5^k \binom n {2 k + 1} = \binom n 1 + 5 \binom n 3 + 5^2 \binom n 5 + \cdots
\end{equation*}
where $F_n$ denotes the $n$-th Fibonacci number and $\binom n k$ denotes a binomial coefficient.

\begin{theorem}
The Gamma function $\Gamma: \mathbb{R}_{>0} \to \mathbb{R}$, when considered on the strictly positive real numbers, is smooth.
\end{theorem}

\begin{theorem}
Let $G$ be a finite group and $p$ be a prime number. If every element of $G$ has order a power of $p$, then $G$ is a $p$-group.
\end{theorem}

Let $S \subseteq \mathbb{R}$ be a non-empty subset that is bounded below, and let $\alpha \in \mathbb{R}$. The following statements are equivalent:
\begin{enumerate}
    \item $\alpha$ is the infimum of $S$.
    \item $\alpha$ is a lower bound for $S$, and for every $\epsilon > 0$, there exists an $x \in S$ such that $x < \alpha + \epsilon$.
\end{enumerate}

\begin{theorem}
The sine of $225^\circ$ is equal to the sine of $\frac{5\pi}{4}$ radians, and both values are equal to $-\frac{\sqrt{2}}{2}$.
\[ \sin 225^\circ = \sin \frac{5\pi}{4} = -\frac{\sqrt{2}}{2} \]
\end{theorem}

\begin{proposition}
Let $(G, \cdot, \preceq)$ be an ordered group with identity element $e$. For any $x \in G$, the following equivalences hold:
\begin{enumerate}
    \item $x \preceq e \iff e \preceq x^{-1}$
    \item $e \preceq x \iff x^{-1} \preceq e$
    \item $x \prec e \iff e \prec x^{-1}$
    \item $e \prec x \iff x^{-1} \prec e$
\end{enumerate}
\end{proposition}

\begin{theorem*}
Let $A$ be a commutative ring with unity, and let $f, g \in A$. The following statements are equivalent:
\begin{enumerate}[label=(\arabic*)]
    \item There exists an induced $A$-algebra homomorphism $h : A_f \to A_g$ between the localizations $A_f$ and $A_g$.
    \item $f$ divides some power of $g$.
    \item There is an inclusion of vanishing sets: $V(f) \subseteq V(g)$. That is, every prime ideal containing $f$ also contains $g$.
    \item There is an inclusion of principal open subsets: $D(f) \supseteq D(g)$.
\end{enumerate}
\end{theorem*}

\begin{theorem}
If a topological space $T=(S, \tau)$ is separable and metacompact, then $T$ is a Lindelöf space.
\end{theorem}

\begin{theorem}
Let $X$ be a Hausdorff topological space, and let $K$ be a compact subspace of $X$. Then the boundary of $K$, denoted $\partial K$, is compact.
\end{theorem}

Let $f: S \times T \to \mathbb A$ be a function. For any finite sets $S$ and $T$, the order of summation can be interchanged, yielding the equality:
\begin{equation*}
\sum_{s \in S} \sum_{t \in T} f(s, t) = \sum_{t \in T} \sum_{s \in S} f(s, t)
\end{equation*}

\begin{theorem}
Let $(S, \vee, \preceq)$ be a join semilattice that is bounded below. If $\mathcal{F}$ is a non-empty set of congruence relations on $S$, then their intersection $\bigcap \mathcal{F}$ is also a congruence relation on $S$.
\end{theorem}

\begin{theorem}
The operation of multiplication on the set of natural numbers $\N$ is commutative, meaning that for all $x, y \in \N$, $x \times y = y \times x$.
\end{theorem}

\begin{theorem}
The definition of the transitive closure of a relation based on finite chains is equivalent to its definition as the union of all finite compositions of the relation.
\end{theorem}

\begin{theorem}
In any group of $6$ people, there exists either a set of $3$ mutual acquaintances (i.e., $3$ people who have all met each other) or a set of $3$ mutual strangers (i.e., $3$ people who have never met each other).
\end{theorem}

\begin{theorem}[Trigonometric Sum Identities]
For any angles $A$ and $B$, the following identities hold:
\begin{align*}
\sin(A+B) &= \sin A \cos B + \cos A \sin B \\
\cos(A+B) &= \cos A \cos B - \sin A \sin B \\
\tan(A+B) &= \frac{\tan A + \tan B}{1 - \tan A \tan B}
\end{align*}
\end{theorem}

The indefinite integral of $x \tan(ax)$ is given by the series expansion
\begin{equation*}
\int x \tan(ax) \, \mathrm{d}x = \frac{1}{a^2} \left( \frac{(ax)^3}{3} + \frac{(ax)^5}{15} + \frac{2(ax)^7}{105} + \cdots + \frac{(-1)^{n - 1} 2^{2n} (2^{2n} - 1) B_{2n} (ax)^{2n + 1}}{(2n + 1)!} + \cdots \right) + C,
\end{equation*}
where $B_{2n}$ denotes the $2n$-th Bernoulli number.

\begin{theorem}
For any two positive rational numbers $a$ and $b$, their product $ab$ is also a positive rational number.
\end{theorem}

\begin{proposition}
Given a segment of a circle, construct the complete circle containing it.
\end{proposition}

\begin{theorem}
Let $\Q$ be the field of rational numbers, and let $\Q[X]$ denote the ring of polynomials over $\Q$ in one indeterminate $X$. If $f(X)$ and $g(X)$ are primitive polynomials in $\Q[X]$, then their product $f(X)g(X)$ is also a primitive polynomial.
\end{theorem}

\begin{theorem}
Let $S$ be a subset of a group $G$. Then there exists a unique smallest normal subgroup of $G$ containing $S$.
\end{theorem}

\begin{theorem}
Let $(S, \tau)$ be a countable complement topology on an uncountable set $S$. Then $(S, \tau)$ is not a $\sigma$-compact space.
\end{theorem}

Let $(X, \Sigma, \mu)$ be a measure space, $(X', \Sigma')$ a measurable space, and $T: X \to X'$ a $\Sigma/\Sigma'$-measurable mapping. If $f: X' \to \overline \R$ is a mapping, then the following statements are equivalent:
\begin{enumerate}
    \item $f$ is integrable with respect to the pushforward measure $T_*\mu$.
    \item $f \circ T$ is $\mu$-integrable.
\end{enumerate}

Let $b \in \mathbb{Z}$ be an integer such that $|b| > 1$, let $k$ be a positive integer, and let $f(X) \in \mathbb{Z}[X]$ be a polynomial with integer coefficients. Suppose there exists an integer $x_k$ satisfying
\begin{align*}
f(x_k) &\equiv 0 \pmod{b^k} \\
\intertext{and}
\gcd(f'(x_k), b) &= 1.
\end{align*}
Then for every integer $l \ge 0$, there exists an integer $x_{k+l}$ such that
\begin{align*}
f(x_{k+l}) &\equiv 0 \pmod{b^{k+l}} \\
x_{k+l} &\equiv x_k \pmod{b^k}
\end{align*}
and this solution $x_{k+l}$ is unique modulo $b^{k+l}$.
Moreover, for any $l \ge 0$, the solutions $x_{k+l}$ and $x_{k+l+1}$ are related by the congruences:
\begin{align*}
x_{k+l+1} &\equiv x_{k+l} - \frac{f(x_{k+l})}{f'(x_{k+l})} \pmod{b^{k+l+1}} \\
x_{k+l+1} &\equiv x_{k+l} \pmod{b^{k+l}}.
\end{align*}

Let $S$ and $T$ be sets, $\RR_S$ and $\RR_T$ be equivalence relations on $S$ and $T$ respectively, and $f \colon S \to T$ be a function. Let $S / \RR_S$ and $T / \RR_T$ denote the quotient sets, and $q_S \colon S \to S / \RR_S$ and $q_T \colon T \to T / \RR_T$ be the canonical projection maps.
Then there exists a function $g \colon S / \RR_S \to T / \RR_T$ such that the following diagram commutes:
\[
\begin{xy} \xymatrix@L+2mu@+1em {
 S \ar[r]^{f} \ar[d]_{q_S} & T \ar[d]^{q_T} \\
S / \RR_S \ar@{-->}[r]_{g} & T / \RR_T
} \end{xy}
\]
(i.e., $q_T \circ f = g \circ q_S$) if and only if for all $x, y \in S$, $x \mathrel{\RR_S} y$ implies $f(x) \mathrel{\RR_T} f(y)$.

\begin{theorem}
For any integers $a$ and $b$, the following statements are equivalent, where $\le$ and $<$ denote the standard ordering and strict ordering on the integers, respectively:
\begin{enumerate}
    \item $a \le b$
    \item $a < b + 1$
\end{enumerate}
\end{theorem}

\begin{theorem}
The set of Gaussian integers $\mathbb{Z}[i]$, under the operation of complex addition, forms a subgroup of the additive group of complex numbers $(\mathbb{C}, +)$.
\end{theorem}

\begin{theorem}
The taxicab metric is a metric.
\end{theorem}

\begin{theorem}
Let $\RR_1, \RR_2 \subseteq N^k$ be $k$-ary relations. If $\RR_1$ and $\RR_2$ are primitive recursive, then the relations $\TT = \neg \RR_1$, $\UU = \RR_1 \land \RR_2$, and $\VV = \RR_1 \lor \RR_2$ are also primitive recursive.
\end{theorem}

\begin{theorem}
For constants $a \neq 0$ and $b$, and for values of $x$ such that $ax+b > 0$, the indefinite integral of the function $\frac{1}{\sqrt{ax+b}}$ is given by
\[ \int \frac{\mathrm{d}x}{\sqrt{ax+b}} = \frac{2\sqrt{ax+b}}{a} + C \]
where $C$ is the constant of integration.
\end{theorem}

Let $x, y, z$ be ordinals and $n$ be a positive natural number. If $x$ is a limit ordinal and $y, z$ are non-zero ordinals, then
\begin{align*}
\left(x^y \times n\right)^z &= x^{y \times z} \times n && \text{if $z$ is a successor ordinal,} \\
\left(x^y \times n\right)^z &= x^{y \times z} && \text{if $z$ is a limit ordinal.}
\end{align*}

\begin{theorem}
A group $G$ is solvable if and only if there exists a normal series $\mathcal{H}$ for $G$ such that all its factor groups are solvable.
\end{theorem}

\begin{theorem}
Let $n$ be a positive integer. Let $r$ be the number of ones in the binary representation of $n$. Then the exponent of the highest power of $2$ dividing $n!$ is $n-r$.
\end{theorem}

\begin{problem}
Prove that $\tanh x \le x$ for all $x \ge 0$.
\end{problem}

\begin{theorem}
Let $(R, +, \circ)$ be a commutative ring. Let $\mathbf{A}$ be an $m \times n$ matrix over $R$ and $\mathbf{B}$ be an $n \times p$ matrix over $R$. Let $\hat{o}_1, \ldots, \hat{o}_{\hat{n}}$ be a finite sequence of elementary row operations applicable to a matrix over $R$ with $m$ rows. Let $\mathbf{A}'$ denote the $m \times n$ matrix that results from applying this sequence of operations to $\mathbf{A}$. Let $\mathbf{C} = \mathbf{A}\mathbf{B}$ be the matrix product of $\mathbf{A}$ and $\mathbf{B}$. Let $\mathbf{C}'$ denote the $m \times p$ matrix that results from applying the same sequence of operations to $\mathbf{C}$. Then, it holds that $\mathbf{C}' = \mathbf{A}' \mathbf{B}$.
\end{theorem}

\begin{theorem}
Let $(S, \preceq)$ be an ordered set. For any two elements $x, y \in S$, if their lower closures are equal, i.e., $x^\preceq = y^\preceq$, then $x = y$.
\end{theorem}

\begin{theorem}
If $\left| x \right| > a$, then
$$
\int \frac{1}{a^2 - x^2} \, dx = \frac{1}{2a} \ln \left( \frac{x + a}{x - a} \right) + C
$$
\end{theorem}

\begin{theorem}
Let $x \in \mathbb{Q}$. The sequence of rational numbers $(x_n)$ defined by $x_n = x^n$ is a null sequence if and only if $|x| < 1$.
\end{theorem}

\begin{theorem}
If $(R, +, \circ)$ is a commutative ring with unity, then the polynomial ring $R[\{X_j : j \in J\}]$ is also a commutative ring with unity.
\end{theorem}

\begin{proposition}
If $A$ is a projection on a Hilbert space $H$, then its complementary projection $I - A$ is also a projection.
\end{proposition}

\begin{theorem}
Let $(R_1, +_1, \circ_1), \ldots, (R_n, +_n, \circ_n)$ be rings, and let $(R, +, \circ) = \prod_{k=1}^n (R_k, +_k, \circ_k)$ be their direct product. For each $k \in \{1, \ldots, n\}$, consider the subset $R'_k = \left\{\left(x_1, \ldots, x_n\right) \in R : x_j = 0 \text{ for all } j \neq k\right\}$. The map $\operatorname{pr}_k: R \to R'_k$, defined by $\operatorname{pr}_k\left(x_1, \ldots, x_n\right) = \left(0, \ldots, x_k, \ldots, 0\right)$, is an epimorphism.
\end{theorem}

\theoremstyle{plain}
\newtheorem*{theorem*}{Theorem}
\begin{theorem*}
The characteristic of a division ring is either $0$ or a prime number.
\end{theorem*}

Let $p$ be a prime number, and let $\Q_p$ be the field of $p$-adic numbers equipped with the $p$-adic norm $\norm{\,\cdot\,}_p$. For any $a \in \Q_p$ and any $\epsilon > 0$, let $B_\epsilon^-(a)$ denote the closed $\epsilon$-ball centered at $a$, and $B_\epsilon(a)$ denote the open $\epsilon$-ball centered at $a$. Prove that:
\begin{enumerate}
    \item For any integer $n$, $B_{p^{-n}}^-(a) = \bigcup_{i=0}^{p-1} B_{p^{-n}}(a + i p^n)$.
    \item For any integer $n$, the collection $\{B_{p^{-n}}(a + i p^n) : i = 0, \dots, p-1\}$ consists of $p$ pairwise disjoint open balls.
\end{enumerate}

\begin{theorem}
Let $S$ be a finite set, and let $\mathscr C$ be a non-empty collection of subsets of $S$. Then $\mathscr C$ is the set of circuits of a matroid on $S$ if and only if it satisfies the following axioms:
\begin{enumerate}
    \item No element of $\mathscr C$ is a proper subset of another element of $\mathscr C$.
    \item If $C_1, C_2 \in \mathscr C$ are distinct, and $x \in C_1 \cap C_2$, then there exists $C_3 \in \mathscr C$ such that $C_3 \subseteq (C_1 \cup C_2) \setminus \{x\}$.
\end{enumerate}
\end{theorem}

\theoremstyle{plain}
\newtheorem*{theorem*}{Theorem}
\begin{theorem*}
For every generalized ordered space $(X, \preceq_X, \tau_X)$, there exists a linearly ordered space $(Y, \preceq_Y, \tau_Y)$ and a mapping $\phi: X \to Y$ such that $\phi$ is both a topological embedding and an order embedding, and its image $\phi(X)$ is closed in $Y$.
\end{theorem*}

\begin{theorem}
Let $G$ be a Hausdorff topological group. If $H$ is a discrete subgroup of $G$, then $H$ is closed in $G$.
\end{theorem}

\begin{theorem}
Let $T$ be a set and $\mathbb S$ be a non-empty collection of sets. If for every $S \in \mathbb S$, $S \subseteq T$, then $\bigcap \mathbb S \subseteq T$.
\end{theorem}

 egin{theorem}
Let $(R, 
orm{\cdot})$ be a normed division ring with unity $1_R$. If its norm $
orm{\cdot}$ is non-Archimedean, then for all positive integers $n \in \mathbb{N}_{>0}$, we have $
orm{n \cdot 1_R} \le 1$, where $n \cdot 1_R$ is defined as the sum of $n$ copies of $1_R$, i.e., $n \cdot 1_R = \underbrace{1_R + 1_R + \dots + 1_R}_{n 	ext{ times}}$.
\end{theorem}

\begin{theorem}
If a relation $\RR$ is symmetric, transitive, and serial, then $\RR$ is reflexive. Consequently, $\RR$ is an equivalence relation.
\end{theorem}

\begin{theorem}
Let $S$ be a set and $\mathcal{R}$ be a transitive relation on $S$. If $T$ is a subset of $S$, then the restriction of $\mathcal{R}$ to $T$, denoted $\mathcal{R}|_T$, is a transitive relation on $T$.
\end{theorem}

\begin{theorem}
Let $(G, \circ)$ be a group. If $N$ and $N'$ are normal subgroups of $G$, then their product $NN'$ is also a normal subgroup of $G$.
\end{theorem}

\begin{theorem}
Let $S$ be a set and $\mathcal B$ be a collection of subsets of $S$. If $\mathcal B$ satisfies the following conditions:
\begin{enumerate}
    \item For any $A_1, A_2 \in \mathcal B$ and any $x \in A_1 \cap A_2$, there exists an $A \in \mathcal B$ such that $x \in A \subseteq A_1 \cap A_2$.
    \item For every $x \in S$, there exists an $A \in \mathcal B$ such that $x \in A$.
\end{enumerate}
Then, the collection $\tau = \left\{ \bigcup \mathcal G \mid \mathcal G \subseteq \mathcal B \right\}$ forms a topology on $S$, and $\mathcal B$ is a basis for the topological space $(S, \tau)$.
\end{theorem}

\begin{theorem}
Every non-empty class $C$ possesses an element $x$ of least rank, formally expressed as $\exists x \in C \colon \forall y \in C \colon \operatorname{rank}(x) \le \operatorname{rank}(y)$, where $\operatorname{rank}(z)$ denotes the rank of the set $z$.
\end{theorem}
